function calculate({ initialDesignLife, assetTypeFactor, materialDegradationRate, environmentalSeverityIndex, operationalLoadFactor, redundancyFactor, routineMaintenanceCostPerYear, majorOverhaulInterval, majorOverhaulCostBase }) {

  // Input Validation and Edge Cases
  if (initialDesignLife <= 0 || assetTypeFactor <= 0 || materialDegradationRate < 0 || environmentalSeverityIndex < 1 || operationalLoadFactor <= 0 || majorOverhaulInterval <= 0) {
    throw new Error('Please ensure all numerical inputs are positive and valid.');
  }
  if (environmentalSeverityIndex > 5) environmentalSeverityIndex = 5;
  if (operationalLoadFactor > 100) operationalLoadFactor = 100;

  const MIN_LIFESPAN = 5; // Minimum plausible lifespan
  const MAX_LIFESPAN = 250; // Maximum plausible lifespan for very robust assets

  // Step 1: Calculate Base Lifespan Adjusted by Asset Type
  // Asset type factor adjusts the theoretical design life based on the inherent robustness/complexity.
  let adjustedDesignLife = initialDesignLife * assetTypeFactor;

  // Step 2: Incorporate Environmental, Operational Load, and Material Degradation Impacts
  // Environmental impact: Higher severity (1-5) accelerates degradation. 1 point = ~4% reduction from base.
  const environmentalImpactFactor = 1 - (environmentalSeverityIndex / 5) * 0.20; // Up to 20% reduction for max severity

  // Operational load impact: Higher utilization (load factor) increases wear and tear. 100% load = ~15% reduction.
  const loadImpactFactor = 1 - (operationalLoadFactor / 100) * 0.15; // Up to 15% reduction for max load

  // Material degradation impact: Direct reduction in years based on annual degradation rate.
  // This applies to the *initial* design life, representing structural integrity loss over time.
  const absoluteDegradationYears = initialDesignLife * (materialDegradationRate / 100); 

  // Combine these factors to get an initial estimation of lifespan reduction
  adjustedDesignLife = adjustedDesignLife * environmentalImpactFactor * loadImpactFactor - absoluteDegradationYears;

  // Step 3: Apply Design Redundancy Factor
  // Redundancy adds robustness, effectively extending lifespan.
  let estimatedOperationalLifespan = adjustedDesignLife * (1 + redundancyFactor);

  // Apply caps to ensure realistic lifespan
  estimatedOperationalLifespan = Math.max(MIN_LIFESPAN, Math.min(MAX_LIFESPAN, estimatedOperationalLifespan));

  // Ensure lifespan is not negative due to aggressive degradation rates, cap at MIN_LIFESPAN
  if (estimatedOperationalLifespan < MIN_LIFESPAN) {
    estimatedOperationalLifespan = MIN_LIFESPAN;
  }

  // Step 4: Calculate Total and Annualized Maintenance Costs
  const totalRoutineCost = estimatedOperationalLifespan * routineMaintenanceCostPerYear;

  const numMajorOverhauls = Math.floor(estimatedOperationalLifespan / majorOverhaulInterval);
  const totalMajorOverhaulCost = numMajorOverhauls * majorOverhaulCostBase;

  const totalEstimatedMaintenanceCost = totalRoutineCost + totalMajorOverhaulCost;

  let annualizedMaintenanceCost = 0;
  if (estimatedOperationalLifespan > 0) {
    annualizedMaintenanceCost = totalEstimatedMaintenanceCost / estimatedOperationalLifespan;
  }

  return {
    estimatedOperationalLifespan: estimatedOperationalLifespan,
    totalEstimatedMaintenanceCost: totalEstimatedMaintenanceCost,
    annualizedMaintenanceCost: annualizedMaintenanceCost
  };
}
