const { carPrice, downPayment, loanTerm, interestRate, resaleValue, leaseTerm, leaseMonthlyPayment, leaseUpfront } = inputs;

// --- Buying Calculation ---
const loanPrincipal = carPrice - downPayment;
const monthlyInterestRate = interestRate / 100 / 12;
let buyMonthlyPayment = 0;

if (loanPrincipal <= 0) {
  buyMonthlyPayment = 0;
} else if (monthlyInterestRate === 0) {
  buyMonthlyPayment = loanPrincipal / loanTerm;
} else {
  const n = loanTerm;
  const r = monthlyInterestRate;
  buyMonthlyPayment = loanPrincipal * (r * Math.pow(1 + r, n)) / (Math.pow(1 + r, n) - 1);
}

const totalPaymentsMade = buyMonthlyPayment * loanTerm;
const totalBuyCost = downPayment + totalPaymentsMade - resaleValue;

// --- Leasing Calculation ---
const totalLeasePayments = leaseMonthlyPayment * leaseTerm;
const totalLeaseCost = leaseUpfront + totalLeasePayments;

// --- Difference ---
const costDifference = totalBuyCost - totalLeaseCost;

return {
  totalBuyCost: isNaN(totalBuyCost) ? 0 : totalBuyCost,
  totalLeaseCost: isNaN(totalLeaseCost) ? 0 : totalLeaseCost,
  costDifference: isNaN(costDifference) ? 0 : costDifference,
  buyMonthlyPayment: isNaN(buyMonthlyPayment) ? 0 : buyMonthlyPayment
};
