const standardBreakerSizes = [15, 20, 25, 30, 35, 40, 45, 50, 60, 70, 80, 90, 100, 110, 125, 150, 175, 200];

// Calculate total connected load current
const totalConnectedLoadAmps = inputs.total_lighting_va / inputs.voltage;

// Apply diversity factor
const diversityFactor = inputs.diversity_factor_percent / 100;
const totalDesignLoadAmps = totalConnectedLoadAmps * diversityFactor;

// Calculate the minimum total breaker ampacity required for the entire lighting load, considering continuous loads
const minimumTotalBreakerAmpacityRequired = totalDesignLoadAmps * inputs.continuous_load_factor;

// Calculate the effective maximum continuous load a single target breaker can safely handle
// If continuous_load_factor is 1.25, a 20A breaker can only continuously carry 16A (20A / 1.25)
const effectiveMaxLoadPerTargetBreaker = inputs.max_circuit_breaker_rating_amps / inputs.continuous_load_factor;

// Determine the minimum number of circuits needed
// This ensures that the design load, when divided among circuits, does not exceed the effective max load per breaker
let minimumNumberOfCircuits = Math.ceil(totalDesignLoadAmps / effectiveMaxLoadPerTargetBreaker);

// Ensure at least one circuit is always present if there's a load, to avoid division by zero or zero circuits for small loads.
if (totalDesignLoadAmps > 0 && minimumNumberOfCircuits === 0) {
    minimumNumberOfCircuits = 1;
} else if (totalDesignLoadAmps === 0) {
    minimumNumberOfCircuits = 0; // No load, no circuits needed
}

// Calculate the actual load per circuit based on the determined number of circuits
const actualLoadPerCircuit = minimumNumberOfCircuits > 0 ? totalDesignLoadAmps / minimumNumberOfCircuits : 0;

// Determine the smallest standard breaker size for each circuit based on actual load and continuous factor
const requiredBreakerRatingBeforeSnap = actualLoadPerCircuit * inputs.continuous_load_factor;

let recommendedBreakerSizePerCircuit = null;
if (requiredBreakerRatingBeforeSnap === 0) {
    recommendedBreakerSizePerCircuit = 0;
} else {
    for (const size of standardBreakerSizes) {
        if (size >= requiredBreakerRatingBeforeSnap) {
            recommendedBreakerSizePerCircuit = size;
            break;
        }
    }
    // If the required rating is higher than the largest standard size, use the next whole number or the rating itself
    if (recommendedBreakerSizePerCircuit === null) {
        recommendedBreakerSizePerCircuit = Math.ceil(requiredBreakerRatingBeforeSnap);
    }
}

return {
    total_connected_load_amps: totalConnectedLoadAmps,
    total_design_load_amps: totalDesignLoadAmps,
    minimum_total_breaker_ampacity_required: minimumTotalBreakerAmpacityRequired,
    minimum_number_of_circuits: minimumNumberOfCircuits,
    recommended_breaker_size_per_circuit: recommendedBreakerSizePerCircuit,
};
