function calculateAmortization(P, r_annual, n_years, extraPayment = 0, paymentsPerYear = 12) {
    const r_periodic = r_annual / 100 / paymentsPerYear; // Periodic interest rate
    const n_total_periods = n_years * paymentsPerYear;   // Total number of periods

    // Calculate original periodic payment (M)
    let M_base;
    if (r_periodic === 0) {
        M_base = P / n_total_periods;
    } else {
        M_base = P * (r_periodic * Math.pow(1 + r_periodic, n_total_periods)) / (Math.pow(1 + r_periodic, n_total_periods) - 1);
    }

    // --- Original Schedule ---
    let originalBalance = P;
    let originalTotalInterest = 0;
    let originalPeriods = 0;

    const MAX_ITERATIONS = n_total_periods * 3; // Cap to prevent infinite loops for very low payments or edge cases

    if (r_periodic > 0 && M_base < originalBalance * r_periodic) {
        // Payment is less than interest due, loan will never be paid off
        originalTotalInterest = Infinity;
        originalPeriods = Infinity;
    } else if (r_periodic === 0 && M_base <= 0) {
        originalTotalInterest = Infinity;
        originalPeriods = Infinity;
    } else {
        for (let i = 0; i < MAX_ITERATIONS && originalBalance > 0.005; i++) { // small threshold for float comparison
            const interestThisPeriod = originalBalance * r_periodic;
            let principalThisPeriod = M_base - interestThisPeriod;

            if (originalBalance <= principalThisPeriod + interestThisPeriod) { // Last payment
                originalTotalInterest += originalBalance * r_periodic;
                originalBalance = 0;
                originalPeriods++;
                break;
            }

            originalBalance -= principalThisPeriod;
            originalTotalInterest += interestThisPeriod;
            originalPeriods++;
        }
    }

    // --- New Schedule with Extra Payments ---
    let newBalance = P;
    let newTotalInterest = 0;
    let newPeriods = 0;
    const M_new = M_base + extraPayment;

    if (r_periodic > 0 && M_new < newBalance * r_periodic) {
        // Payment is less than interest due, loan will never be paid off
        newTotalInterest = Infinity;
        newPeriods = Infinity;
    } else if (r_periodic === 0 && M_new <= 0) {
        newTotalInterest = Infinity;
        newPeriods = Infinity;
    } else {
        for (let i = 0; i < MAX_ITERATIONS && newBalance > 0.005; i++) { // small threshold for float comparison
            const interestThisPeriod = newBalance * r_periodic;
            let principalThisPeriod = M_new - interestThisPeriod;

            if (newBalance <= principalThisPeriod + interestThisPeriod) { // Last payment
                newTotalInterest += newBalance * r_periodic;
                newBalance = 0;
                newPeriods++;
                break;
            }

            newBalance -= principalThisPeriod;
            newTotalInterest += interestThisPeriod;
            newPeriods++;
        }
    }

    // Handle cases where loan technically doesn't pay off within MAX_ITERATIONS
    if (originalPeriods >= MAX_ITERATIONS && originalBalance > 0.005 && originalPeriods !== Infinity) {
        originalTotalInterest = Infinity;
        originalPeriods = Infinity;
    }
    if (newPeriods >= MAX_ITERATIONS && newBalance > 0.005 && newPeriods !== Infinity) {
        newTotalInterest = Infinity;
        newPeriods = Infinity;
    }

    // If M_base calculation yields infinity/NaN due to r_periodic approaching 0 and large n_total_periods
    if (isNaN(M_base) || !isFinite(M_base) || M_base < 0) {
        M_base = (r_periodic === 0) ? P / n_total_periods : Infinity; // Fallback for 0% interest or unsolvable
        originalTotalInterest = (r_periodic === 0) ? 0 : Infinity;
        originalPeriods = (r_periodic === 0) ? n_total_periods : Infinity;
    }

    const interestSaved = isFinite(originalTotalInterest) && isFinite(newTotalInterest) ? originalTotalInterest - newTotalInterest : NaN;
    const timeSavedPeriods = isFinite(originalPeriods) && isFinite(newPeriods) ? originalPeriods - newPeriods : NaN;

    return {
        originalPeriodicPayment: M_base,
        originalTotalInterest: originalTotalInterest,
        originalPayoffPeriods: originalPeriods,
        newTotalPeriodicPayment: M_new,
        newTotalInterest: newTotalInterest,
        newPayoffPeriods: newPeriods,
        interestSaved: interestSaved,
        timeSavedPeriods: timeSavedPeriods
    };
}

const loanAmount = inputs.loanAmount;
const annualInterestRate = inputs.annualInterestRate;
const loanTermYears = inputs.loanTermYears;
const paymentFrequency = inputs.paymentFrequency; // payments per year
const extraPaymentAmount = inputs.extraPaymentAmount;

// Guard against invalid inputs that could lead to division by zero or negative results
if (loanAmount <= 0 || annualInterestRate < 0 || loanTermYears <= 0 || paymentFrequency <= 0) {
    return {
        originalPeriodicPayment: NaN,
        originalTotalInterest: NaN,
        originalPayoffPeriods: NaN,
        newTotalPeriodicPayment: NaN,
        newTotalInterest: NaN,
        newPayoffPeriods: NaN,
        interestSaved: NaN,
        timeSavedPeriods: NaN
    };
}

return calculateAmortization(loanAmount, annualInterestRate, loanTermYears, extraPaymentAmount, paymentFrequency);
