function calculate(inputs) {
    const loanAmount = inputs.loanAmount;
    const annualInterestRate = inputs.interestRate; // as percentage, e.g., 5 for 5%
    const loanTermYears = inputs.loanTerm;
    const paymentsPerYear = inputs.paymentsPerYear;
    const extraPayment = inputs.extraPayment || 0;

    if (loanAmount <= 0 || annualInterestRate < 0 || loanTermYears <= 0 || paymentsPerYear <= 0) {
        throw new Error("Invalid input. Loan amount, interest rate, loan term, and payments per year must be positive.");
    }

    const periodicInterestRate = (annualInterestRate / 100) / paymentsPerYear;
    const totalPeriodsOriginal = loanTermYears * paymentsPerYear;

    let regularPayment;
    if (periodicInterestRate === 0) {
        regularPayment = loanAmount / totalPeriodsOriginal;
    } else {
        const i_plus_1_pow_n = Math.pow(1 + periodicInterestRate, totalPeriodsOriginal);
        regularPayment = loanAmount * (periodicInterestRate * i_plus_1_pow_n) / (i_plus_1_pow_n - 1);
    }

    let remainingBalance = loanAmount;
    let totalInterestPaid = 0;
    let totalPrincipalPaid = 0;
    const schedule = [];
    let currentPaymentNumber = 1;

    const effectivePaymentPerPeriod = regularPayment + extraPayment;

    // Cap iterations to prevent infinite loops, particularly with tiny remaining balances and floating point arithmetic
    const maxIterations = totalPeriodsOriginal * 2 + 5; 

    while (remainingBalance > 0.01 && currentPaymentNumber <= maxIterations) {
        const interestForPeriod = remainingBalance * periodicInterestRate;
        let principalPaidThisPeriod = effectivePaymentPerPeriod - interestForPeriod;

        // Adjust for the last payment, where remaining balance might be less than the calculated principal portion
        if (principalPaidThisPeriod > remainingBalance) {
            principalPaidThisPeriod = remainingBalance;
        }

        // If payment is too small to cover interest (e.g., due to negative extraPayment or extreme edge case),
        // ensure principal paid is not negative and at least interest is covered if possible.
        if (principalPaidThisPeriod < 0 && effectivePaymentPerPeriod > 0) {
            principalPaidThisPeriod = 0; // All of payment goes to interest
        } else if (effectivePaymentPerPeriod <= 0 && remainingBalance > 0) {
             // If no payment is made but balance exists, this loan won't amortize. Break.
             break;
        }

        const actualPaymentMade = interestForPeriod + principalPaidThisPeriod;

        totalInterestPaid += interestForPeriod;
        totalPrincipalPaid += principalPaidThisPeriod;
        remainingBalance -= principalPaidThisPeriod;

        schedule.push({
            paymentNumber: currentPaymentNumber,
            startingBalance: parseFloat((remainingBalance + principalPaidThisPeriod).toFixed(2)),
            paymentAmount: parseFloat(actualPaymentMade.toFixed(2)),
            interestPaid: parseFloat(interestForPeriod.toFixed(2)),
            principalPaid: parseFloat(principalPaidThisPeriod.toFixed(2)),
            endingBalance: parseFloat(remainingBalance.toFixed(2))
        });

        if (remainingBalance <= 0.01) { 
            remainingBalance = 0; // Ensure it ends exactly at zero for display
            break;
        }
        currentPaymentNumber++;
    }

    const finalTotalAmountPaid = totalPrincipalPaid + totalInterestPaid;

    return {
        regularPayment: parseFloat(regularPayment.toFixed(2)),
        totalInterestPaid: parseFloat(totalInterestPaid.toFixed(2)),
        totalAmountPaid: parseFloat(finalTotalAmountPaid.toFixed(2)),
        amortizationSchedule: schedule
    };
}
