const loanAmount = inputs.loanAmount;
const loanTermYears = inputs.loanTermYears;
const upfrontFeesA = inputs.upfrontFeesA;
const aprA = inputs.aprA;
const upfrontFeesB = inputs.upfrontFeesB;
const aprB = inputs.aprB;

const n = loanTermYears * 12; // Total number of payments

// Function to calculate monthly payment given principal, annual APR, and total payments
const calculateMonthlyPayment = (principal, annualAPR, totalPayments) => {
    if (totalPayments <= 0 || principal <= 0) {
        return 0; // Cannot calculate payment for zero or negative term/principal
    }
    if (annualAPR === 0) {
        return principal / totalPayments;
    }
    const monthlyRate = (annualAPR / 100) / 12;
    // P * [r(1+r)^n] / [(1+r)^n – 1]
    const numerator = monthlyRate * Math.pow(1 + monthlyRate, totalPayments);
    const denominator = Math.pow(1 + monthlyRate, totalPayments) - 1;
    if (denominator === 0) {
        return 0; // Avoid division by zero in case of extremely high rates or errors
    }
    return principal * (numerator / denominator);
};

let monthlyPaymentA = 0;
let totalCostA = 0;
if (loanAmount > 0 && loanTermYears > 0 && aprA >= 0) {
    monthlyPaymentA = calculateMonthlyPayment(loanAmount, aprA, n);
    totalCostA = (monthlyPaymentA * n) + upfrontFeesA;
} else {
    monthlyPaymentA = 0;
    totalCostA = upfrontFeesA; // Only fees apply if no loan calculation is possible
}

let monthlyPaymentB = 0;
let totalCostB = 0;
if (loanAmount > 0 && loanTermYears > 0 && aprB >= 0) {
    monthlyPaymentB = calculateMonthlyPayment(loanAmount, aprB, n);
    totalCostB = (monthlyPaymentB * n) + upfrontFeesB;
} else {
    monthlyPaymentB = 0;
    totalCostB = upfrontFeesB; // Only fees apply if no loan calculation is possible
}

// A positive costDifference means Loan A is more expensive than Loan B.
// A negative costDifference means Loan A is cheaper than Loan B.
const costDifference = totalCostA - totalCostB; 

return {
    monthlyPaymentA: monthlyPaymentA,
    totalCostA: totalCostA,
    monthlyPaymentB: monthlyPaymentB,
    totalCostB: totalCostB,
    costDifference: costDifference
};
