function calculatePMT(principal, annualRate, months) {
    if (annualRate <= 0) return principal / months; // Handle 0 or negative interest rate case
    const monthlyRate = annualRate / 12 / 100;
    // Ensure months is not 0 for calculation
    if (months <= 0) return 0; 
    const numerator = principal * monthlyRate * Math.pow(1 + monthlyRate, months);
    const denominator = Math.pow(1 + monthlyRate, months) - 1;
    return numerator / denominator;
}

const {
    currentLoanBalance,
    currentInterestRate,
    currentLoanTermRemaining,
    newInterestRate,
    newLoanTerm,
    closingCosts
} = inputs;

// Calculate current monthly payment
const currentMonthlyPayment = calculatePMT(currentLoanBalance, currentInterestRate, currentLoanTermRemaining);

// Calculate new monthly payment (assuming new loan is for the same balance)
const newMonthlyPayment = calculatePMT(currentLoanBalance, newInterestRate, newLoanTerm);

let monthlySavings = currentMonthlyPayment - newMonthlyPayment;
let breakevenMonths = Infinity;
let breakevenYears = Infinity;

if (monthlySavings > 0 && closingCosts >= 0) {
    breakevenMonths = closingCosts / monthlySavings;
    breakevenYears = breakevenMonths / 12;
} else if (closingCosts === 0 && monthlySavings >= 0) {
    // If no closing costs, breakeven is immediate or savings start immediately
    breakevenMonths = 0;
    breakevenYears = 0;
}

// If monthly savings are negative or zero, but closing costs are positive,
// there is no beneficial breakeven point.
// If monthly savings are positive, but closing costs are negative (e.g., lender credit),
// it implies an immediate benefit, so breakeven is 0.
// Cap monthly savings at 0 if negative, as it indicates a loss, not a saving.
if (monthlySavings < 0) {
    monthlySavings = 0; 
}

return {
    monthlySavings: monthlySavings,
    breakevenMonths: breakevenMonths,
    breakevenYears: breakevenYears
};
