function calculate(inputs) {
    const {
        deploymentDurationDays,
        avgDailyRevenuePreDisruption,
        estSalesReductionFootTraffic,
        estSalesReductionOperationalHours,
        costOfGoodsSoldPercentage,
        dailyFixedOperatingCosts,
        supplyChainCostIncreasePercentage,
        estAdditionalDailyOperationalCosts,
        businessVulnerabilityFactor
    } = inputs;

    // Input validation and sanitization
    const duration = Math.max(0, deploymentDurationDays || 0);
    const dailyRevenue = Math.max(0, avgDailyRevenuePreDisruption || 0);
    const salesReductFT = Math.min(100, Math.max(0, estSalesReductionFootTraffic || 0));
    const salesReductOH = Math.min(100, Math.max(0, estSalesReductionOperationalHours || 0));
    const cogsPrc = Math.min(100, Math.max(0, costOfGoodsSoldPercentage || 0));
    const fixedCostsDaily = Math.max(0, dailyFixedOperatingCosts || 0);
    const supplyChainIncreasePrc = Math.min(100, Math.max(0, supplyChainCostIncreasePercentage || 0));
    const additionalCostsDaily = Math.max(0, estAdditionalDailyOperationalCosts || 0);
    const vulnFactor = Math.min(2.0, Math.max(0.5, businessVulnerabilityFactor || 1.0));

    if (duration === 0 || dailyRevenue === 0) {
        return {
            totalEconomicLoss: 0,
            lostGrossProfitContribution: 0,
            unrecoveredFixedCostExposure: 0,
            supplyChainImpactCost: 0,
            totalAdditionalOperationalCosts: 0
        };
    }

    // Step 1: Calculate the combined percentage reduction in sales volume.
    // This accounts for both foot traffic and operational hour restrictions multiplicatively.
    const effectiveSalesRetentionFactor = (1 - salesReductFT / 100) * (1 - salesReductOH / 100);
    const combinedSalesReductionPrc = 1 - effectiveSalesRetentionFactor;

    // Step 2: Calculate the total gross revenue lost due to reduced sales volume.
    const grossRevenueLossPerDay = dailyRevenue * combinedSalesReductionPrc;
    const totalGrossRevenueLoss = grossRevenueLossPerDay * duration;

    // Step 3: Calculate the lost gross profit contribution.
    // Gross profit is revenue minus COGS. We lose the profit margin on sales that didn't happen.
    const lostGrossProfitContribution = totalGrossRevenueLoss * (1 - cogsPrc / 100) * vulnFactor;

    // Step 4: Calculate unrecovered fixed cost exposure.
    // Fixed costs are incurred regardless of sales volume.
    const unrecoveredFixedCostExposure = fixedCostsDaily * duration;

    // Step 5: Calculate supply chain disruption costs.
    // This is an increase in COGS applied to the *original* daily revenue for the duration.
    // It represents the increased cost of goods that would have been sold or purchased.
    const originalDailyCOGS = dailyRevenue * (cogsPrc / 100);
    const supplyChainImpactCost = originalDailyCOGS * (supplyChainIncreasePrc / 100) * duration;

    // Step 6: Calculate additional operational costs.
    // Costs like increased security, emergency staffing, spoiled inventory, etc.
    const totalAdditionalOperationalCosts = additionalCostsDaily * duration;

    // Step 7: Sum all components for the total economic loss.
    const totalEconomicLoss = lostGrossProfitContribution + unrecoveredFixedCostExposure + supplyChainImpactCost + totalAdditionalOperationalCosts;

    return {
        totalEconomicLoss: totalEconomicLoss,
        lostGrossProfitContribution: lostGrossProfitContribution,
        unrecoveredFixedCostExposure: unrecoveredFixedCostExposure,
        supplyChainImpactCost: supplyChainImpactCost,
        totalAdditionalOperationalCosts: totalAdditionalOperationalCosts
    };
}
