function calculate({ eventDurationHours, normalDailyRevenue, disruptionImpactPercentage, averageEmployeeHourlyWage, employeesAffected, dailyFixedOperatingCosts, recoveryMarketingBudget, inventorySpoilageLoss }) {

  // Ensure non-negative inputs
  eventDurationHours = Math.max(0, eventDurationHours || 0);
  normalDailyRevenue = Math.max(0, normalDailyRevenue || 0);
  disruptionImpactPercentage = Math.max(0, Math.min(100, disruptionImpactPercentage || 0)); // Cap between 0 and 100
  averageEmployeeHourlyWage = Math.max(0, averageEmployeeHourlyWage || 0);
  employeesAffected = Math.max(0, employeesAffected || 0);
  dailyFixedOperatingCosts = Math.max(0, dailyFixedOperatingCosts || 0);
  recoveryMarketingBudget = Math.max(0, recoveryMarketingBudget || 0);
  inventorySpoilageLoss = Math.max(0, inventorySpoilageLoss || 0);

  // Step 1: Calculate Estimated Direct Revenue Loss
  const dailyRevenueLossRate = normalDailyRevenue * (disruptionImpactPercentage / 100);
  const estimatedRevenueLoss = (dailyRevenueLossRate / 24) * eventDurationHours; // Prorate daily loss over event duration

  // Step 2: Calculate Estimated Labor Costs
  const estimatedLaborCosts = averageEmployeeHourlyWage * employeesAffected * eventDurationHours;

  // Step 3: Calculate Estimated Pro-rata Fixed Costs Incurred
  const estimatedFixedCosts = (dailyFixedOperatingCosts / 24) * eventDurationHours;

  // Step 4: Calculate Estimated Recovery & Inventory Costs
  const estimatedRecoveryAndInventoryCosts = recoveryMarketingBudget + inventorySpoilageLoss;

  // Step 5: Calculate Total Estimated Disruption Cost
  const totalEstimatedDisruptionCost = estimatedRevenueLoss + estimatedLaborCosts + estimatedFixedCosts + estimatedRecoveryAndInventoryCosts;

  return {
    estimatedRevenueLoss,
    estimatedLaborCosts,
    estimatedFixedCosts,
    estimatedRecoveryAndInventoryCosts,
    totalEstimatedDisruptionCost
  };
}
