function calculate({ communityPopulation, gdpPerCapita, industry1RevenueShare, industry1ClimateImpactSeverity, industry2RevenueShare, industry2ClimateImpactSeverity, disasterRecoveryBudgetShare, adaptationInvestmentEfficiency }) {

  // Input validation and default values to ensure robustness
  communityPopulation = Math.max(0, communityPopulation || 0);
  gdpPerCapita = Math.max(0, gdpPerCapita || 0);
  industry1RevenueShare = Math.min(100, Math.max(0, industry1RevenueShare || 0));
  industry1ClimateImpactSeverity = Math.min(100, Math.max(0, industry1ClimateImpactSeverity || 0));
  industry2RevenueShare = Math.min(100, Math.max(0, industry2RevenueShare || 0));
  industry2ClimateImpactSeverity = Math.min(100, Math.max(0, industry2ClimateImpactSeverity || 0));
  disasterRecoveryBudgetShare = Math.min(10, Math.max(0, disasterRecoveryBudgetShare || 0)); // Cap at 10% for realism, as higher shares are rare for annual budgets
  adaptationInvestmentEfficiency = Math.min(1, Math.max(0, adaptationInvestmentEfficiency || 0));

  // Step 1: Calculate Total Local GDP
  const totalGDP = communityPopulation * gdpPerCapita;

  // Handle cases where totalGDP is zero to prevent division by zero or nonsensical results
  if (totalGDP === 0) {
    return {
      totalEconomicImpact: 0,
      netRecoveryCost: 0,
      gdpReductionPercentage: 0,
      impactOnIndustry1: 0,
      impactOnIndustry2: 0
    };
  }

  // Step 2: Determine Industry Revenue Contributions
  const industry1Revenue = totalGDP * (industry1RevenueShare / 100);
  const industry2Revenue = totalGDP * (industry2RevenueShare / 100);

  // Step 3: Estimate Raw Climate Event Impacts
  const rawImpactIndustry1 = industry1Revenue * (industry1ClimateImpactSeverity / 100);
  const rawImpactIndustry2 = industry2Revenue * (industry2ClimateImpactSeverity / 100);

  // Sum the raw impacts from dominant industries before adaptation
  const combinedRawIndustryImpact = rawImpactIndustry1 + rawImpactIndustry2;

  // Step 4: Adjust for Adaptation Investments
  // Adaptation reduces the initial impact directly. An efficiency factor of 0.3 means a 30% reduction.
  const totalEconomicImpact = combinedRawIndustryImpact * (1 - adaptationInvestmentEfficiency);
  const impactOnIndustry1 = rawImpactIndustry1 * (1 - adaptationInvestmentEfficiency);
  const impactOnIndustry2 = rawImpactIndustry2 * (1 - adaptationInvestmentEfficiency);

  // Step 5: Apply Disaster Recovery Budget
  const disasterBudgetAmount = totalGDP * (disasterRecoveryBudgetShare / 100);

  // Calculate the net financial cost after applying the recovery budget.
  // The net cost cannot be negative; if the budget covers the impact, the cost is 0.
  const netRecoveryCost = Math.max(0, totalEconomicImpact - disasterBudgetAmount);

  // Step 6: Calculate Overall GDP Reduction
  const gdpReductionPercentage = (totalEconomicImpact / totalGDP) * 100;

  return {
    totalEconomicImpact,
    netRecoveryCost,
    gdpReductionPercentage,
    impactOnIndustry1,
    impactOnIndustry2
  };
}
