const numberOfVisitors = inputs.numberOfVisitors || 0;
const avgVisitorSpending = inputs.avgVisitorSpending || 0;
const localVendorParticipationRate = inputs.localVendorParticipationRate || 0; // %
const totalVolunteerHours = inputs.totalVolunteerHours || 0;
const avgVolunteerHourValue = inputs.avgVolunteerHourValue || 0;
const estimatedMediaReach = inputs.estimatedMediaReach || 0;
const mediaAdEquivalentValue = inputs.mediaAdEquivalentValue || 0; // per 1000 reach
const communitySatisfactionScore = inputs.communitySatisfactionScore || 0;
const directLocalJobsCreated = inputs.directLocalJobsCreated || 0;
const avgJobWage = inputs.avgJobWage || 0;

// --- Economic Calculations ---

// 1. Total Visitor Spending Impact (direct spending by all visitors)
const visitorSpendingImpact = numberOfVisitors * avgVisitorSpending;

// 2. Local Visitor Spending Portion (portion of visitor spending that likely goes to local businesses)
const localVisitorSpendingPortion = visitorSpendingImpact * (localVendorParticipationRate / 100);

// 3. Wages paid to local temporary employees
const localEmploymentWages = directLocalJobsCreated * avgJobWage;

// 4. Direct Local Economic Stimulus (money directly staying/generated within the local economy)
const localEconomicStimulus = localVisitorSpendingPortion + localEmploymentWages;

// 5. Estimated Media Value (monetizing earned media exposure)
const estimatedMediaValue = (estimatedMediaReach / 1000) * mediaAdEquivalentValue;

// 6. Volunteer Contribution Value (monetizing 'sweat equity')
const volunteerContributionValue = totalVolunteerHours * avgVolunteerHourValue;

// 7. Total Estimated Economic Impact (aggregation of all monetizable benefits)
const totalEconomicImpact = visitorSpendingImpact + localEmploymentWages + estimatedMediaValue + volunteerContributionValue;

// --- Social Calculations ---

// 1. Community Satisfaction Component (uses raw input score, 1-10)
const socialSatisfaction = communitySatisfactionScore;

// 2. Volunteer Engagement Component (normalize total volunteer hours to a 1-10 scale)
// Example: 500 hours = 10 points, 250 hours = 5 points. Capped at 10 to prevent infinite scaling.
const socialVolunteerEngagement = Math.min(10, totalVolunteerHours / 50);

// 3. Local Participation Component (normalize localVendorParticipationRate % to a 1-10 scale)
// Example: 100% -> 10 points, 50% -> 5 points.
const socialLocalParticipation = localVendorParticipationRate / 10;

// 4. Social Cohesion & Community Engagement Index (weighted average of social factors)
// Weights can be adjusted based on perceived importance. Satisfaction (0.5), Volunteer Engagement (0.25), Local Participation (0.25).
const socialCohesionIndex = (socialSatisfaction * 0.5) + (socialVolunteerEngagement * 0.25) + (socialLocalParticipation * 0.25);

// Ensure the index is within a reasonable range (e.g., capped between 1.0 and 10.0)
const finalSocialCohesionIndex = Math.max(1.0, Math.min(10.0, socialCohesionIndex));

return {
    totalEconomicImpact: totalEconomicImpact,
    localEconomicStimulus: localEconomicStimulus,
    estimatedMediaValue: estimatedMediaValue,
    volunteerContributionValue: volunteerContributionValue,
    socialCohesionIndex: finalSocialCohesionIndex
};
