function calculate({ initialProjectSpending, localSpendingRatio, directJobsCreated, avgAnnualDirectWage, marginalPropensityToConsume, leakageToSavingsAndImports, localTaxRate }) {

    // Input validation and clamping
    initialProjectSpending = Math.max(0, initialProjectSpending || 0);
    localSpendingRatio = Math.min(1, Math.max(0, localSpendingRatio || 0));
    directJobsCreated = Math.max(0, directJobsCreated || 0);
    avgAnnualDirectWage = Math.max(0, avgAnnualDirectWage || 0);
    marginalPropensityToConsume = Math.min(1, Math.max(0, marginalPropensityToConsume || 0));
    leakageToSavingsAndImports = Math.min(1, Math.max(0, leakageToSavingsAndImports || 0));
    localTaxRate = Math.min(1, Math.max(0, localTaxRate || 0));

    // Step 1: Calculate the direct local economic injection
    const localDirectSpending = initialProjectSpending * localSpendingRatio;
    const directLocalWages = directJobsCreated * avgAnnualDirectWage;
    const totalDirectLocalInjection = localDirectSpending + directLocalWages;

    // If no initial injection, no impact
    if (totalDirectLocalInjection === 0 && directJobsCreated === 0) {
        return {
            economicMultiplier: 0,
            totalEconomicImpact: 0,
            totalJobsCreated: 0,
            totalLocalIncomeGenerated: 0,
            totalLocalTaxRevenue: 0
        };
    }

    // Step 2: Calculate the effective Marginal Propensity to Consume locally
    // This accounts for income spent locally after leakages to savings, non-local purchases, and local taxes.
    const effectiveRetentionRate = (1 - leakageToSavingsAndImports) * (1 - localTaxRate);
    const effectiveMPCLocally = marginalPropensityToConsume * effectiveRetentionRate;

    // Ensure effectiveMPCLocally does not lead to negative or infinite multiplier
    const denominator = 1 - effectiveMPCLocally;
    let economicMultiplier;
    if (denominator <= 0) {
        // If MPC is too high or retention is perfect, assume a practical cap to the multiplier.
        economicMultiplier = 100; // A reasonable upper bound for practical applications
    } else {
        economicMultiplier = 1 / denominator;
    }

    // Multiplier must be at least 1 (the initial injection itself)
    economicMultiplier = Math.max(1, economicMultiplier);

    // Step 3: Calculate Total Economic Impact
    const totalEconomicImpact = totalDirectLocalInjection * economicMultiplier;

    // Step 4: Calculate Total Jobs Created (simplified assumption)
    // Assumes the job multiplier is similar to the economic activity multiplier for simplicity
    const totalJobsCreated = directJobsCreated * economicMultiplier;

    // Step 5: Calculate Total Local Income Generated
    // This is the portion of the total economic impact that remains as income within the local economy
    // after accounting for non-consumption leakages (savings, non-local purchases).
    const totalLocalIncomeGenerated = totalEconomicImpact * (1 - leakageToSavingsAndImports);

    // Step 6: Calculate Total Local Tax Revenue
    // This is the estimated tax revenue generated from the total locally retained income.
    const totalLocalTaxRevenue = totalLocalIncomeGenerated * localTaxRate;

    return {
        economicMultiplier: economicMultiplier,
        totalEconomicImpact: totalEconomicImpact,
        totalJobsCreated: totalJobsCreated,
        totalLocalIncomeGenerated: totalLocalIncomeGenerated,
        totalLocalTaxRevenue: totalLocalTaxRevenue
    };
}
