function calculate({ totalPopulation, vulnerablePopulationPercentage, forecastSeverityIndex, emergencyDurationHours, currentUrgentCareCapacity, currentShelterCapacity, infrastructureResilienceScore, publicComplianceRate }) {

  // Input validation and normalization
  totalPopulation = Math.max(0, totalPopulation || 0);
  vulnerablePopulationPercentage = Math.min(100, Math.max(0, vulnerablePopulationPercentage || 0)) / 100;
  forecastSeverityIndex = Math.min(10, Math.max(1, forecastSeverityIndex || 1));
  emergencyDurationHours = Math.max(0, emergencyDurationHours || 0);
  currentUrgentCareCapacity = Math.max(0, currentUrgentCareCapacity || 0);
  currentShelterCapacity = Math.max(0, currentShelterCapacity || 0);
  infrastructureResilienceScore = Math.min(1, Math.max(0, infrastructureResilienceScore || 0));
  publicComplianceRate = Math.min(100, Math.max(0, publicComplianceRate || 0)) / 100;

  // --- Constants and Base Rates ---
  // These are illustrative and would be data-driven in a real-world system and calibrated locally
  const BASE_SHELTER_DEMAND_PER_1000_POP_PER_HR = 0.05; // Base people seeking shelter per 1000 population per hour
  const BASE_UC_DEMAND_PER_1000_POP_PER_DAY = 0.2;     // Base people needing urgent care per 1000 population per day
  const VULNERABLE_MULTIPLIER = 2.5;                  // Vulnerable population has higher impact on demand
  const SEVERITY_FACTOR = 0.1;                        // How much severity index impacts demand (0.1 means 10% increase per point)
  const RESILIENCE_IMPACT = 0.3;                      // How much resilience score reduces demand (0.3 means 30% reduction at max resilience)
  const COMPLIANCE_IMPACT = 0.2;                      // How much compliance reduces demand (0.2 means 20% reduction at max compliance)

  // --- Step 1: Calculate Adjusted Demand Factors ---
  // Severity increases demand, resilience and compliance decrease it.
  const demandMultiplier = 
    1 + (forecastSeverityIndex * SEVERITY_FACTOR) - (infrastructureResilienceScore * RESILIENCE_IMPACT) - (publicComplianceRate * COMPLIANCE_IMPACT);

  // Ensure demand multiplier is not negative or excessively low, cap to prevent under-estimation.
  const adjustedDemandMultiplier = Math.max(0.5, demandMultiplier); 

  // --- Step 2: Project Shelter Demand ---
  // Shelter demand is influenced by total population, vulnerable population, duration, and adjusted demand multiplier.
  // Vulnerable population is estimated to have a higher likelihood of needing shelter.
  const totalVulnerablePopulation = totalPopulation * vulnerablePopulationPercentage;
  const totalNonVulnerablePopulation = totalPopulation - totalVulnerablePopulation;

  // Base demand from non-vulnerable (lower rate)
  let projectedShelterDemand = (totalNonVulnerablePopulation / 1000) * BASE_SHELTER_DEMAND_PER_1000_POP_PER_HR * emergencyDurationHours;

  // Additional demand from vulnerable population (higher rate)
  projectedShelterDemand += (totalVulnerablePopulation / 1000) * BASE_SHELTER_DEMAND_PER_1000_POP_PER_HR * VULNERABLE_MULTIPLIER * emergencyDurationHours;

  // Apply the overall adjusted demand multiplier
  projectedShelterDemand *= adjustedDemandMultiplier;

  // --- Step 3: Project Urgent Care Demand ---
  // UC demand is daily, influenced by total population, vulnerable population, and adjusted demand multiplier.
  // Note: For daily UC demand, we consider the average daily need over the emergency duration.
  const averageDailyUrgentCareDemandPer1000 = BASE_UC_DEMAND_PER_1000_POP_PER_DAY * (1 + (vulnerablePopulationPercentage * (VULNERABLE_MULTIPLIER - 1)));
  let projectedUrgentCareDemand = (totalPopulation / 1000) * averageDailyUrgentCareDemandPer1000;

  // Apply the overall adjusted demand multiplier
  projectedUrgentCareDemand *= adjustedDemandMultiplier;

  // --- Step 4: Calculate Shortfalls/Surpluses ---
  const shelterShortfall = projectedShelterDemand - currentShelterCapacity;
  const urgentCareShortfall = projectedUrgentCareDemand - currentUrgentCareCapacity;

  // --- Step 5: Calculate Overall Resource Strain Index ---
  // This index reflects the severity of resource strain, normalized to a 0-10 scale.
  // We use a scaled average of shortfalls relative to capacity, with higher weight for larger shortfalls.
  let shelterStrain = 0;
  if (currentShelterCapacity > 0) {
    shelterStrain = Math.max(0, shelterShortfall / currentShelterCapacity);
  } else if (projectedShelterDemand > 0) { // No capacity, but demand exists
    shelterStrain = 5; // Max individual strain contribution if capacity is zero but demand is present
  }

  let ucStrain = 0;
  if (currentUrgentCareCapacity > 0) {
    ucStrain = Math.max(0, urgentCareShortfall / currentUrgentCareCapacity);
  } else if (projectedUrgentCareDemand > 0) {
    ucStrain = 5; // Max individual strain contribution if capacity is zero but demand is present
  }

  // Cap individual strains to avoid extreme outliers distorting the average too much, but allow significant strain.
  shelterStrain = Math.min(5, shelterStrain); // Cap at 500% strain for individual component
  ucStrain = Math.min(5, ucStrain);

  // Combine strains, weighing them. For simplicity, equal weight for now.
  const averageStrain = (shelterStrain + ucStrain) / 2;

  // Scale to 0-10 index. If individual strain max is 5, then average max is 5. So 5*2 = 10.
  let overallResourceStrainIndex = averageStrain * 2; 
  overallResourceStrainIndex = Math.min(10, Math.max(0, overallResourceStrainIndex)); // Ensure 0-10 range

  return {
    projectedShelterDemand: Math.round(projectedShelterDemand),
    shelterShortfall: Math.round(shelterShortfall),
    projectedUrgentCareDemand: Math.round(projectedUrgentCareDemand),
    urgentCareShortfall: Math.round(urgentCareShortfall),
    overallResourceStrainIndex: parseFloat(overallResourceStrainIndex.toFixed(1))
  };
}
