function calculateWellbeingImpact(inputs) {
    const { 
        budgetAllocationPercentage, 
        communityEngagementScore, 
        economicOpportunityImpact, 
        housingAffordabilityImpact, 
        publicSafetyPerceptionChange, 
        socialCohesionEffect, 
        environmentalSustainabilityImpact, 
        equityAccessibilityImpact 
    } = inputs;

    // Input Validation and Clamping
    const budget = Math.max(0, Math.min(100, budgetAllocationPercentage));
    const engagement = Math.max(1, Math.min(10, communityEngagementScore));

    const ecoOpp = Math.max(-5, Math.min(5, economicOpportunityImpact));
    const housing = Math.max(-5, Math.min(5, housingAffordabilityImpact));
    const safety = Math.max(-5, Math.min(5, publicSafetyPerceptionChange));
    const cohesion = Math.max(-5, Math.min(5, socialCohesionEffect));
    const environment = Math.max(-5, Math.min(5, environmentalSustainabilityImpact));
    const equity = Math.max(-5, Math.min(5, equityAccessibilityImpact));

    // Step 1: Normalize budget and engagement to multipliers
    // Budget multiplier: larger budget means potentially larger impact, but caps at a certain point
    const budgetMultiplier = 1 + (budget / 50);
    // Engagement multiplier: high engagement amplifies positive impacts, mitigates negative ones (less resistance)
    const engagementFactor = engagement / 10; // 0.1 to 1.0

    // Step 2: Calculate raw scores for each indicator
    // Economic Stability: weighted by direct opportunity and negative housing impact
    let economicStabilityRaw = (ecoOpp * 8) + (housing * -4);

    // Social Cohesion: direct impact of policy on community bonds
    let socialCohesionRaw = cohesion * 9;

    // Public Safety: direct impact on perception of safety
    let publicSafetyRaw = safety * 9;

    // Equity & Access: direct equity impact and additional negative from housing displacement/unaffordability
    let equityAndAccessRaw = (equity * 8) + (housing * -3);

    // Environmental Impact: direct impact on sustainability
    let environmentalImpactRaw = environment * 6;

    // Step 3: Apply modifiers based on budget and engagement
    // Positive impacts are amplified by engagement, negative impacts are less severe with high engagement
    economicStabilityRaw = economicStabilityRaw >= 0 ? economicStabilityRaw * (1 + engagementFactor * 0.5) : economicStabilityRaw * (1 - engagementFactor * 0.2);
    socialCohesionRaw = socialCohesionRaw >= 0 ? socialCohesionRaw * (1 + engagementFactor * 0.5) : socialCohesionRaw * (1 - engagementFactor * 0.2);
    publicSafetyRaw = publicSafetyRaw >= 0 ? publicSafetyRaw * (1 + engagementFactor * 0.5) : publicSafetyRaw * (1 - engagementFactor * 0.2);
    equityAndAccessRaw = equityAndAccessRaw >= 0 ? equityAndAccessRaw * (1 + engagementFactor * 0.5) : equityAndAccessRaw * (1 - engagementFactor * 0.2);
    environmentalImpactRaw = environmentalImpactRaw >= 0 ? environmentalImpactRaw * (1 + engagementFactor * 0.5) : environmentalImpactRaw * (1 - engagementFactor * 0.2);

    // All raw scores are then scaled by the budget multiplier
    const economicStabilityFinal = economicStabilityRaw * budgetMultiplier;
    const socialCohesionFinal = socialCohesionRaw * budgetMultiplier;
    const publicSafetyFinal = publicSafetyRaw * budgetMultiplier;
    const equityAndAccessFinal = equityAndAccessRaw * budgetMultiplier;
    const environmentalImpactFinal = environmentalImpactRaw * budgetMultiplier;

    // Step 4: Calculate Overall Wellbeing Score as a weighted average of final component scores
    const overallWellbeing = (
        economicStabilityFinal * 0.30 + 
        socialCohesionFinal * 0.25 + 
        publicSafetyFinal * 0.20 + 
        equityAndAccessFinal * 0.15 + 
        environmentalImpactFinal * 0.10
    );

    // Step 5: Cap output scores to predefined ranges
    const cap = (score, min, max) => Math.max(min, Math.min(max, score));

    return {
        overallWellbeingScore: cap(overallWellbeing, -100, 100),
        economicStabilityImpact: cap(economicStabilityFinal, -50, 50),
        socialCohesionIndexChange: cap(socialCohesionFinal, -50, 50),
        publicSafetyEffectiveness: cap(publicSafetyFinal, -50, 50),
        equityAndAccessScore: cap(equityAndAccessFinal, -50, 50)
    };
}
