const gender = inputs.gender.toLowerCase();
const age = parseFloat(inputs.age);
const weight = parseFloat(inputs.weight); // kg
const height = parseFloat(inputs.height); // cm
const activityLevel = inputs.activityLevel.toLowerCase();
const goal = inputs.goal.toLowerCase();

if (isNaN(age) || isNaN(weight) || isNaN(height)) {
    return { error: 'Please enter valid numerical values for age, weight, and height.' };
}

let bmr;
// Mifflin-St Jeor Equation
if (gender === 'male') {
    bmr = (10 * weight) + (6.25 * height) - (5 * age) + 5;
} else if (gender === 'female') {
    bmr = (10 * weight) + (6.25 * height) - (5 * age) - 161;
} else {
    return { error: 'Please enter either "Male" or "Female" for gender.' };
}

let activityFactor;
switch (activityLevel) {
    case 'sedentary':
        activityFactor = 1.2;
        break;
    case 'lightly active':
        activityFactor = 1.375;
        break;
    case 'moderately active':
        activityFactor = 1.55;
        break;
    case 'very active':
        activityFactor = 1.725;
        break;
    case 'extra active':
        activityFactor = 1.9;
        break;
    default:
        return { error: 'Please enter a valid activity level: Sedentary, Lightly Active, Moderately Active, Very Active, or Extra Active.' };
}

let tdee = bmr * activityFactor;
let targetCalories = tdee;
let proteinPct, fatPct, carbPct;

switch (goal) {
    case 'weight loss':
        targetCalories = tdee * 0.8; // 20% calorie deficit
        proteinPct = 0.35; // 35%
        fatPct = 0.25;   // 25%
        carbPct = 0.40;  // 40%
        break;
    case 'muscle gain':
        targetCalories = tdee * 1.1; // 10% calorie surplus
        proteinPct = 0.30; // 30%
        fatPct = 0.25;   // 25%
        carbPct = 0.45;  // 45%
        break;
    case 'maintenance':
        proteinPct = 0.25; // 25%
        fatPct = 0.30;   // 30%
        carbPct = 0.45;  // 45%
        break;
    default:
        return { error: 'Please enter a valid goal: Weight Loss, Muscle Gain, or Maintenance.' };
}

const proteinCalories = targetCalories * proteinPct;
const fatCalories = targetCalories * fatPct;
const carbCalories = targetCalories * carbPct;

// Calories per gram: Protein = 4, Carbs = 4, Fats = 9
const proteinGrams = proteinCalories / 4;
const fatGrams = fatCalories / 9;
const carbGrams = carbCalories / 4;

return {
    totalCalories: Math.round(targetCalories),
    proteinGrams: Math.round(proteinGrams),
    carbGrams: Math.round(carbGrams),
    fatGrams: Math.round(fatGrams)
};
