/**
 * Calculates daily caloric needs and macronutrient breakdown.
 * Uses Mifflin-St Jeor equation for BMR and adjusts for activity and goals.
 *
 * @param {object} inputs - The input values from the user.
 * @param {string} inputs.sex - 'male' or 'female'.
 * @param {number} inputs.age - Age in years.
 * @param {number} inputs.height - Height in centimeters.
 * @param {number} inputs.weight - Weight in kilograms.
 * @param {string} inputs.activityLevel - One of 'sedentary', 'lightlyActive', 'moderatelyActive', 'veryActive', 'extraActive'.
 * @param {string} inputs.fitnessGoal - One of 'maintain', 'mildWeightLoss', 'weightLoss', 'aggressiveWeightLoss', 'mildWeightGain', 'weightGain'.
 * @returns {object} An object containing totalCalories, proteinGrams, carbGrams, fatGrams.
 */
function calculate(inputs) {
  const { sex, age, height, weight, activityLevel, fitnessGoal } = inputs;

  // Step 1: Calculate Basal Metabolic Rate (BMR) using Mifflin-St Jeor Equation
  let bmr;
  if (sex === 'male') {
    bmr = (10 * weight) + (6.25 * height) - (5 * age) + 5;
  } else { // female
    bmr = (10 * weight) + (6.25 * height) - (5 * age) - 161;
  }

  // Step 2: Determine Physical Activity Level (PAL) factor
  let palFactor;
  switch (activityLevel) {
    case 'sedentary':
      palFactor = 1.2;
      break;
    case 'lightlyActive':
      palFactor = 1.375;
      break;
    case 'moderatelyActive':
      palFactor = 1.55;
      break;
    case 'veryActive':
      palFactor = 1.725;
      break;
    case 'extraActive':
      palFactor = 1.9;
      break;
    default:
      palFactor = 1.2; // Default to sedentary
  }

  // Step 3: Calculate Total Daily Energy Expenditure (TDEE)
  let tdee = bmr * palFactor;

  // Step 4: Adjust TDEE based on Fitness Goal
  let adjustment = 0;
  switch (fitnessGoal) {
    case 'mildWeightLoss':
      adjustment = -300;
      break;
    case 'weightLoss':
      adjustment = -500;
      break;
    case 'aggressiveWeightLoss':
      adjustment = -750;
      break;
    case 'mildWeightGain':
      adjustment = 300;
      break;
    case 'weightGain':
      adjustment = 500;
      break;
    case 'maintain':
    default:
      adjustment = 0;
  }

  let totalCalories = tdee + adjustment;

  // Safety cap for aggressive weight loss: prevent deficit from exceeding 25% of TDEE
  // This ensures the deficit from the goal is not too extreme relative to TDEE.
  if (fitnessGoal === 'aggressiveWeightLoss' && totalCalories < tdee * 0.75) {
    totalCalories = tdee * 0.75;
  }

  // Minimum calorie intake caps for health and safety
  if (sex === 'female' && totalCalories < 1200) {
    totalCalories = 1200;
  }
  if (sex === 'male' && totalCalories < 1500) {
    totalCalories = 1500;
  }

  // Step 5: Calculate Macronutrient Breakdown (Protein, Carbs, Fats)
  // General recommendations for percentages. Protein is often higher for weight loss/gain.
  let proteinPct, carbPct, fatPct;

  if (fitnessGoal === 'weightLoss' || fitnessGoal === 'aggressiveWeightLoss' || fitnessGoal === 'weightGain') {
    proteinPct = 0.35; // Higher protein for muscle preservation/growth
    fatPct = 0.25;
    carbPct = 1 - proteinPct - fatPct; // Remaining for carbs (0.40)
  } else { // maintain, mildWeightLoss, mildWeightGain
    proteinPct = 0.25; // Balanced approach
    fatPct = 0.30;
    carbPct = 1 - proteinPct - fatPct; // Remaining for carbs (0.45)
  }

  // Convert calories to grams (Protein: 4 kcal/g, Carbs: 4 kcal/g, Fat: 9 kcal/g)
  let proteinGrams = (totalCalories * proteinPct) / 4;
  let carbGrams = (totalCalories * carbPct) / 4;
  let fatGrams = (totalCalories * fatPct) / 9;

  return {
    totalCalories: Math.round(totalCalories),
    proteinGrams: Math.round(proteinGrams),
    carbGrams: Math.round(carbGrams),
    fatGrams: Math.round(fatGrams)
  };
}
