function calculateMacros(inputs) {
  const gender = inputs.gender.toLowerCase();
  const age = parseFloat(inputs.age);
  const weight_lbs = parseFloat(inputs.weight_lbs);
  const height_feet = parseFloat(inputs.height_feet);
  const height_inches = parseFloat(inputs.height_inches);
  const activity_level = inputs.activity_level.toLowerCase();
  const goal = inputs.goal.toLowerCase();

  // Input validation (basic)
  if (isNaN(age) || isNaN(weight_lbs) || isNaN(height_feet) || isNaN(height_inches) || age <= 0 || weight_lbs <= 0 || height_feet < 0 || height_inches < 0) {
    throw new Error('Please enter valid positive numbers for age, weight, and height.');
  }
  if (!['male', 'female'].includes(gender)) {
    throw new Error('Gender must be Male or Female.');
  }

  // Convert weight to kg and height to cm
  const weight_kg = weight_lbs * 0.453592;
  const total_height_inches = (height_feet * 12) + height_inches;
  const height_cm = total_height_inches * 2.54;

  // 1. Calculate BMR (Basal Metabolic Rate) using Mifflin-St Jeor Equation
  let bmr;
  if (gender === 'male') {
    bmr = (10 * weight_kg) + (6.25 * height_cm) - (5 * age) + 5;
  } else {
    bmr = (10 * weight_kg) + (6.25 * height_cm) - (5 * age) - 161;
  }

  // 2. Determine Activity Multiplier
  let activity_multiplier;
  if (activity_level.includes('sedentary')) {
    activity_multiplier = 1.2;
  } else if (activity_level.includes('lightly active')) {
    activity_multiplier = 1.375;
  } else if (activity_level.includes('moderately active')) {
    activity_multiplier = 1.55;
  } else if (activity_level.includes('very active')) {
    activity_multiplier = 1.725;
  } else if (activity_level.includes('extremely active')) {
    activity_multiplier = 1.9;
  } else {
    activity_multiplier = 1.55; // Default to moderately active if somehow not matched
  }

  // 3. Calculate TDEE (Total Daily Energy Expenditure)
  let tdee = bmr * activity_multiplier;

  // 4. Adjust TDEE based on Goal
  let target_calories;
  if (goal === 'maintain weight') {
    target_calories = tdee;
  } else if (goal === 'lose weight') {
    target_calories = tdee * 0.80; // 20% caloric deficit
    target_calories = Math.max(1200, target_calories); // Cap minimum calories for health and safety
  } else if (goal === 'gain weight') {
    target_calories = tdee * 1.10; // 10% caloric surplus
  } else {
    target_calories = tdee; // Default to maintain if not matched
  }

  // 5. Macronutrient Allocation
  // Protein: 0.8-1.0g per pound of body weight for active individuals
  // Using 0.9g/lb for a general recommendation. Ensure a minimum of 50g.
  let protein_grams = weight_lbs * 0.9;
  protein_grams = Math.max(50, protein_grams); 
  const protein_calories = protein_grams * 4; // 4 kcal/gram

  // Fats: Aim for 25% of total calories. Ensure a minimum of 30g for hormonal health.
  const min_fat_calories_for_health = 30 * 9; // 30g * 9 kcal/gram
  let fat_calories = target_calories * 0.25;
  let fat_grams = fat_calories / 9;

  // Carbs: Remaining calories
  let carb_calories = target_calories - protein_calories - (fat_grams * 9);
  let carb_grams = carb_calories / 4; // 4 kcal/gram

  // Conditional adjustment: If carbs are negative, re-adjust fat down to make space, but respect fat minimum.
  if (carb_grams < 0) {
    const needed_fat_reduction_calories = -carb_calories;
    // Reduce fat calories, but not below the minimum required for health.
    fat_calories = Math.max(min_fat_calories_for_health, fat_calories - needed_fat_reduction_calories);
    fat_grams = fat_calories / 9;

    // Recalculate carb calories after fat adjustment
    carb_calories = target_calories - protein_calories - (fat_grams * 9);
    carb_grams = Math.max(0, carb_calories / 4); // Ensure non-negative after re-calculation
  }

  // Final check to ensure protein and fat don't exceed target calories significantly if a very low target is set
  // This can happen if target_calories is extremely low, and the fixed protein/fat minimums are relatively high.
  // In such cases, we prioritize protein, then fat, then carbs.
  const current_total_macro_calories = protein_calories + (fat_grams * 9) + (carb_grams * 4);
  if (current_total_macro_calories > target_calories && carb_grams === 0) {
      // If carbs are already at 0 and total calories are still over, adjust fat down to meet target
      const excess_calories = current_total_macro_calories - target_calories;
      let new_fat_calories = (fat_grams * 9) - excess_calories;
      new_fat_calories = Math.max(min_fat_calories_for_health, new_fat_calories);
      fat_grams = new_fat_calories / 9;
  }

  return {
    total_calories: target_calories,
    protein_grams: protein_grams,
    fat_grams: fat_grams,
    carb_grams: carb_grams
  };
}
