let bmr;
if (gender === 'male') {
    bmr = (10 * weight) + (6.25 * height) - (5 * age) + 5;
} else { // female
    bmr = (10 * weight) + (6.25 * height) - (5 * age) - 161;
}

let activityFactor;
switch (activityLevel.split(' ')[0]) { // Take the first word for matching
    case 'sedentary':
        activityFactor = 1.2;
        break;
    case 'lightly-active':
        activityFactor = 1.375;
        break;
    case 'moderately-active':
        activityFactor = 1.55;
        break;
    case 'very-active':
        activityFactor = 1.725;
        break;
    case 'extra-active':
        activityFactor = 1.9;
        break;
    default:
        activityFactor = 1.2; // Default to sedentary if somehow invalid
}

let tdee = bmr * activityFactor;

let calorieAdjustment = 0;
switch (goal.split(' ')[0]) { // Take the first word for matching
    case 'mild-weight-loss':
        calorieAdjustment = -250; // Approx 0.25kg/week
        break;
    case 'weight-loss':
        calorieAdjustment = -500; // Approx 0.5kg/week
        break;
    case 'extreme-weight-loss':
        calorieAdjustment = -750; // Approx 0.75kg/week
        break;
    case 'mild-weight-gain':
        calorieAdjustment = 250; // Approx 0.25kg/week
        break;
    case 'weight-gain':
        calorieAdjustment = 500; // Approx 0.5kg/week
        break;
    case 'muscle-gain':
        calorieAdjustment = 300; // Moderate surplus for muscle gain
        break;
    case 'maintain':
    default:
        calorieAdjustment = 0;
        break;
}

let targetTDEE = tdee + calorieAdjustment;

// Ensure a minimum calorie intake for health, especially for extreme weight loss
const minCalorieFemale = 1200;
const minCalorieMale = 1500;
if (gender === 'female' && targetTDEE < minCalorieFemale) {
    targetTDEE = minCalorieFemale;
} else if (gender === 'male' && targetTDEE < minCalorieMale) {
    targetTDEE = minCalorieMale;
}

// Default Macronutrient Ratios (Protein, Carb, Fat percentages as decimals)
let proteinRatio, carbRatio, fatRatio;

const defaultRatios = {
    'maintain': { P: 0.30, C: 0.40, F: 0.30 },
    'mild-weight-loss': { P: 0.35, C: 0.35, F: 0.30 },
    'weight-loss': { P: 0.40, C: 0.30, F: 0.30 },
    'extreme-weight-loss': { P: 0.45, C: 0.25, F: 0.30 },
    'mild-weight-gain': { P: 0.25, C: 0.45, F: 0.30 },
    'weight-gain': { P: 0.25, C: 0.50, F: 0.25 },
    'muscle-gain': { P: 0.35, C: 0.40, F: 0.25 }
};

const defaultForGoal = defaultRatios[goal.split(' ')[0]] || defaultRatios['maintain'];

// Check if custom protein and fat ratios are provided and are valid
const hasValidCustomProtein = typeof customProteinRatio === 'number' && customProteinRatio >= 0.05 && customProteinRatio <= 0.70;
const hasValidCustomFat = typeof customFatRatio === 'number' && customFatRatio >= 0.05 && customFatRatio <= 0.50;

if (hasValidCustomProtein && hasValidCustomFat) {
    const calculatedCarb = 1.0 - customProteinRatio - customFatRatio;
    // Ensure calculated carb is also within reasonable bounds (e.g., at least 5% and not excessively high)
    if (calculatedCarb >= 0.05 && calculatedCarb <= 0.80) { // Carb can be up to 80%
        proteinRatio = customProteinRatio;
        fatRatio = customFatRatio;
        carbRatio = calculatedCarb;
    } else {
        // Fallback to default if custom combination leads to invalid carb ratio
        proteinRatio = defaultForGoal.P;
        carbRatio = defaultForGoal.C;
        fatRatio = defaultForGoal.F;
    }
} else {
    // Use default ratios if custom ones are not fully provided or invalid
    proteinRatio = defaultForGoal.P;
    carbRatio = defaultForGoal.C;
    fatRatio = defaultForGoal.F;
}

// Final normalization and safety checks for ratios
const MIN_RATIO_PERCENT = 0.05; // 5% minimum for any macro

// Apply minimums and re-normalize
let ratios = [proteinRatio, carbRatio, fatRatio];
let sum = 0;
let numBelowMin = 0;

for(let i=0; i<ratios.length; i++) {
    if(ratios[i] < MIN_RATIO_PERCENT) {
        ratios[i] = MIN_RATIO_PERCENT;
        numBelowMin++;
    }
    sum += ratios[i];
}

// If adjusting for minimums caused the sum to exceed 1, re-distribute excess from non-minimums
if (sum > 1.0) {
    let excess = sum - 1.0;
    let availableForReduction = 0;
    let reductionTargets = [];

    // Find macros that are above the minimum and can be reduced
    for(let i=0; i<ratios.length; i++) {
        if (ratios[i] > MIN_RATIO_PERCENT) {
            availableForReduction += (ratios[i] - MIN_RATIO_PERCENT);
            reductionTargets.push(i);
        }
    }

    // Distribute the excess reduction proportionally among targets
    if (availableForReduction > 0) {
        for(let i of reductionTargets) {
            ratios[i] -= excess * ((ratios[i] - MIN_RATIO_PERCENT) / availableForReduction);
        }
    } else { // Fallback if all are at min or something went wrong, just normalize evenly
        ratios = [1/3, 1/3, 1/3];
    }
}

// If sum is still not 1.0 (e.g., due to floating point error or all started below min), re-normalize
sum = ratios[0] + ratios[1] + ratios[2];
if (sum !== 0) { // Avoid division by zero
    proteinRatio = ratios[0] / sum;
    carbRatio = ratios[1] / sum;
    fatRatio = ratios[2] / sum;
} else { // Should not happen with MIN_RATIO_PERCENT logic, but as a final failsafe
    proteinRatio = 1/3; carbRatio = 1/3; fatRatio = 1/3;
}

// Calculate calories for each macro
const proteinCalories = targetTDEE * proteinRatio;
const fatCalories = targetTDEE * fatRatio;
const carbCalories = targetTDEE * carbRatio;

// Calculate grams for each macro
const proteinGrams = proteinCalories / 4; // 4 kcal/g for protein
const fatGrams = fatCalories / 9;     // 9 kcal/g for fat
const carbGrams = carbCalories / 4;   // 4 kcal/g for carbohydrates

return {
    tdee: targetTDEE,
    proteinGrams: proteinGrams,
    fatGrams: fatGrams,
    carbGrams: carbGrams,
    proteinCalories: proteinCalories,
    fatCalories: fatCalories,
    carbCalories: carbCalories
};
