const gender = inputs.gender.toLowerCase();
const age = Number(inputs.age);
const weight = Number(inputs.weight); // kg
const height = Number(inputs.height); // cm
const activityLevel = inputs.activityLevel.toLowerCase();
const fitnessGoal = inputs.fitnessGoal.toLowerCase();

// 1. Calculate BMR (Mifflin-St Jeor Equation)
let bmr;
if (gender === 'male') {
  bmr = (10 * weight) + (6.25 * height) - (5 * age) + 5;
} else if (gender === 'female') {
  bmr = (10 * weight) + (6.25 * height) - (5 * age) - 161;
} else {
  throw new Error('Invalid gender. Please enter "Male" or "Female".');
}

// 2. Determine Activity Factor
let activityFactor;
switch (activityLevel) {
  case 'sedentary':
    activityFactor = 1.2;
    break;
  case 'light':
    activityFactor = 1.375;
    break;
  case 'moderate':
    activityFactor = 1.55;
    break;
  case 'very active':
    activityFactor = 1.725;
    break;
  case 'extra active':
    activityFactor = 1.9;
    break;
  default:
    throw new Error('Invalid activity level. Please choose from Sedentary, Light, Moderate, Very Active, or Extra Active.');
}

// 3. Calculate TDEE (Total Daily Energy Expenditure)
const tdee = bmr * activityFactor;

// 4. Adjust TDEE based on Fitness Goal
let totalDailyCalories;
let proteinFactorPerKg;
let fatPercentageOfTotal;

switch (fitnessGoal) {
  case 'weight loss':
    totalDailyCalories = tdee * 0.85; // 15% deficit
    // Cap minimum calories for health
    totalDailyCalories = gender === 'female' ? Math.max(totalDailyCalories, 1200) : Math.max(totalDailyCalories, 1500);
    proteinFactorPerKg = 2.2; // g/kg for muscle preservation
    fatPercentageOfTotal = 0.25; // 25% of calories from fat
    break;
  case 'muscle gain':
    totalDailyCalories = tdee * 1.1; // 10% surplus
    proteinFactorPerKg = 1.8; // g/kg for muscle synthesis
    fatPercentageOfTotal = 0.25; // 25% of calories from fat
    break;
  case 'maintenance':
    totalDailyCalories = tdee;
    proteinFactorPerKg = 1.8; // g/kg for general health/maintenance
    fatPercentageOfTotal = 0.28; // Slightly higher for general well-being
    break;
  default:
    throw new Error('Invalid fitness goal. Please choose from Weight Loss, Muscle Gain, or Maintenance.');
}

// 5. Calculate Macronutrient Breakdown
let proteinGrams = weight * proteinFactorPerKg;
let proteinCals = proteinGrams * 4;

// Ensure protein calories don't exceed total daily calories in extreme cases
if (proteinCals >= totalDailyCalories) {
    proteinCals = totalDailyCalories * 0.5; // Cap protein at 50% if too high
    proteinGrams = proteinCals / 4;
}

let fatCals = totalDailyCalories * fatPercentageOfTotal;
let fatGrams = fatCals / 9;

const minFatGrams = weight * 0.8; // Minimum 0.8g/kg body weight for hormonal health
if (fatGrams < minFatGrams) {
    fatGrams = minFatGrams;
    fatCals = fatGrams * 9; // Recalculate fatCals if fatGrams was capped up
}

// Ensure remaining calories for carbs are not negative
let remainingCalsForCarbs = totalDailyCalories - proteinCals - fatCals;
let carbCals = Math.max(0, remainingCalsForCarbs);
let carbGrams = carbCals / 4;

// 6. Calculate Percentages based on actual distributed calories
const totalMacroCals = proteinCals + fatCals + carbCals;

let proteinPercent = (proteinCals / totalMacroCals) * 100;
let carbPercent = (carbCals / totalMacroCals) * 100;
let fatPercent = (fatCals / totalMacroCals) * 100;

// Adjust percentages to sum up to 100% if minor floating point differences
// Only if totalMacroCals is not zero to avoid division by zero
if (totalMacroCals > 0) {
    const sumPercent = proteinPercent + carbPercent + fatPercent;
    if (Math.abs(sumPercent - 100) > 0.1) { // Check if sum is significantly off from 100
        const adjustmentFactor = 100 / sumPercent;
        proteinPercent *= adjustmentFactor;
        carbPercent *= adjustmentFactor;
        fatPercent *= adjustmentFactor;
    }
}

return {
  totalDailyCalories: totalDailyCalories,
  proteinGrams: proteinGrams,
  proteinPercent: proteinPercent,
  carbGrams: carbGrams,
  carbPercent: carbPercent,
  fatGrams: fatGrams,
  fatPercent: fatPercent
};
