function calculateMacros(inputs) {
  const { gender, age, weight, height, activity_level, goal } = inputs;

  // Step 1: Calculate Basal Metabolic Rate (BMR) using Mifflin-St Jeor Equation
  let bmr;
  if (gender === 'male') {
    bmr = (10 * weight) + (6.25 * height) - (5 * age) + 5;
  } else { // female
    bmr = (10 * weight) + (6.25 * height) - (5 * age) - 161;
  }

  // Step 2: Determine Total Daily Energy Expenditure (TDEE) based on activity level
  let activityFactor;
  switch (activity_level) {
    case 'sedentary':
      activityFactor = 1.2;
      break;
    case 'lightly_active':
      activityFactor = 1.375;
      break;
    case 'moderately_active':
      activityFactor = 1.55;
      break;
    case 'very_active':
      activityFactor = 1.725;
      break;
    case 'extra_active':
      activityFactor = 1.9;
      break;
    default:
      activityFactor = 1.2; // Default to sedentary
  }

  let tdee = bmr * activityFactor;

  // Step 3: Adjust TDEE based on the user's goal
  let targetCalories = tdee;
  const MIN_CALORIES = 1200; // Safety cap for extreme deficits, generally not recommended below this for women
  const MIN_CALORIES_MALE = 1500; // Safety cap for men

  if (goal === 'weight_loss') {
    const deficit = 400; // A moderate deficit for sustainable weight loss
    targetCalories = tdee - deficit;
    // Ensure calories don't go too low
    if (gender === 'female') {
      targetCalories = Math.max(MIN_CALORIES, targetCalories);
    } else {
      targetCalories = Math.max(MIN_CALORIES_MALE, targetCalories);
    }
  } else if (goal === 'muscle_gain') {
    const surplus = 300; // A moderate surplus for muscle gain with minimal fat gain
    targetCalories = tdee + surplus;
  } else { // maintenance
    // targetCalories remains TDEE
  }

  // Step 4: Determine Macronutrient Ratios based on the goal
  let proteinPct, carbsPct, fatPct;

  if (goal === 'weight_loss') {
    // Higher protein for satiety and muscle preservation, moderate carbs for energy, moderate fat
    proteinPct = 40;
    carbsPct = 30;
    fatPct = 30;
  } else if (goal === 'muscle_gain') {
    // High protein for muscle synthesis, higher carbs for energy and recovery, lower fat
    proteinPct = 35;
    carbsPct = 45;
    fatPct = 20;
  } else { // maintenance
    // Balanced approach for overall health and energy
    proteinPct = 30;
    carbsPct = 40;
    fatPct = 30;
  }

  // Step 5: Calculate calories for each macronutrient
  const proteinCals = targetCalories * (proteinPct / 100);
  const carbsCals = targetCalories * (carbsPct / 100);
  const fatCals = targetCalories * (fatPct / 100);

  // Step 6: Convert macronutrient calories to grams
  // Protein: 4 kcal/g, Carbs: 4 kcal/g, Fat: 9 kcal/g
  const proteinGrams = proteinCals / 4;
  const carbsGrams = carbsCals / 4;
  const fatGrams = fatCals / 9;

  return {
    total_calories: targetCalories,
    protein_grams: proteinGrams,
    carbs_grams: carbsGrams,
    fat_grams: fatGrams,
    protein_percentage: proteinPct,
    carbs_percentage: carbsPct,
    fat_percentage: fatPct
  };
}
