function calculate({ policyChangeMagnitude, policyDirection, politicalStabilityIndex, geopoliticalTensionLevel, regulatoryOversightImpact, regulatoryOversightSentiment, marketVolatilityIndex, corporateEarningsOutlook }) {

    // Input validation and default values (though all are required by UI)
    policyChangeMagnitude = Math.max(0, Math.min(10, policyChangeMagnitude || 0));
    policyDirection = Math.max(-1, Math.min(1, policyDirection || 0));
    politicalStabilityIndex = Math.max(0, Math.min(10, politicalStabilityIndex || 0));
    geopoliticalTensionLevel = Math.max(0, Math.min(10, geopoliticalTensionLevel || 0));
    regulatoryOversightImpact = Math.max(0, Math.min(10, regulatoryOversightImpact || 0));
    regulatoryOversightSentiment = Math.max(-1, Math.min(1, regulatoryOversightSentiment || 0));
    marketVolatilityIndex = Math.max(5, Math.min(100, marketVolatilityIndex || 0));
    corporateEarningsOutlook = Math.max(-2, Math.min(2, corporateEarningsOutlook || 0));

    // 1. Normalize Input Factors to a -1 to 1 range (where 1 is maximum positive impact on confidence)

    // Economic Policy Impact (EPI): Combines magnitude and direction
    const policyMagnitudeScaled = policyChangeMagnitude / 10; // 0 to 1
    const epi = policyMagnitudeScaled * policyDirection; // -1 to 1

    // Political Stability Impact (PSI): 0-10 -> -1 to 1 (5 is neutral, 0 is -1, 10 is 1)
    const psi = (politicalStabilityIndex - 5) / 5; // -1 to 1

    // Geopolitical Tension Impact (GTI): 0-10 -> 1 to -1 (5 is neutral, 0 is 1, 10 is -1)
    // Higher tension means lower confidence, so we invert the normalized value
    const gti = -((geopoliticalTensionLevel - 5) / 5); // 1 to -1

    // Regulatory Oversight Impact (ROI): Combines impact level and sentiment
    const regulatoryMagnitudeScaled = regulatoryOversightImpact / 10; // 0 to 1
    const roi = regulatoryMagnitudeScaled * regulatoryOversightSentiment; // -1 to 1

    // Market Volatility Impact (MVI): VIX typically 10-80 for normal/stressed conditions.
    // Map VIX: VIX 10 = 1 (low volatility, high confidence), VIX 20 = 0 (neutral), VIX 80 = -1 (high volatility, low confidence)
    // Normalize VIX to 0-1 first: (VIX - minVIX) / (maxVIX - minVIX)
    // Then map to -1 to 1: 1 - 2 * normalizedVIX
    // Using a more focused range for typical impact, e.g., 10-80 is impactful
    const minVIXConsidered = 10;
    const maxVIXConsidered = 80;
    const clampedVIX = Math.max(minVIXConsidered, Math.min(maxVIXConsidered, marketVolatilityIndex));
    const normalizedVIX = (clampedVIX - minVIXConsidered) / (maxVIXConsidered - minVIXConsidered); // 0 to 1
    const mvi = 1 - (2 * normalizedVIX); // 1 to -1

    // Corporate Earnings Impact (CEI): -2 to 2 -> -1 to 1
    const cei = corporateEarningsOutlook / 2; // -1 to 1

    // 2. Apply Expert-Weighted Coefficients
    // These weights reflect the relative importance of each factor. Total sum should be 1.0
    const W_EPI = 0.20; // Economic Policy is highly influential
    const W_PSI = 0.15; // Political Stability is fundamental
    const W_GTI = 0.15; // Geopolitical events have significant, often immediate impact
    const W_ROI = 0.15; // Regulatory news can create or destroy trust
    const W_MVI = 0.20; // Market Volatility is a direct measure of sentiment/uncertainty
    const W_CEI = 0.15; // Corporate health underpins market fundamentals

    // 3. Aggregate Weighted Scores
    const weightedSum = 
        (epi * W_EPI) + 
        (psi * W_PSI) + 
        (gti * W_GTI) + 
        (roi * W_ROI) + 
        (mvi * W_MVI) + 
        (cei * W_CEI);

    // 4. Scale to Market Confidence Shift (-100 to +100 points)
    const marketConfidenceShift = weightedSum * 100; // Directly scales the -1 to 1 range to -100 to 100

    // 5. Derive Investor Behavior Confidence Index (0-100)
    // A score of 50 represents neutral confidence.
    const investorBehaviorIndex = Math.max(0, Math.min(100, (marketConfidenceShift / 2) + 50));

    return {
        marketConfidenceShift: parseFloat(marketConfidenceShift.toFixed(2)),
        investorBehaviorIndex: parseFloat(investorBehaviorIndex.toFixed(2))
    };
}
