formula:
const { h, t, k, fm, Pa, Ma } = inputs;

const SR_UNREINFORCED_LIMIT = 30; // Max slenderness for unreinforced masonry without special considerations
const SR_CODE_TRANSITION = 99;   // Transition point for Ce factor in some codes
const CE_CONSTANT_1 = 140;       // Constant for Ce calculation
const CE_CONSTANT_2 = 70;        // Constant for Ce calculation
const FA_FACTOR = 0.25;          // Factor for basic allowable axial stress (e.g., 0.25*f'm)
const FB_FACTOR = 0.33;          // Factor for basic allowable bending stress (e.g., 0.33*f'm)

// 1. Basic Input Validation
if (h <= 0 || t <= 0 || k <= 0 || fm <= 0) {
    return {
        effective_height: null,
        slenderness_ratio: null,
        allowable_axial_comp_stress: null,
        applied_axial_comp_stress: null,
        applied_bending_stress: null,
        interaction_ratio: null,
        status: "Fail (Invalid input: h, t, k, or f'm must be > 0)"
    };
}

// 2. Effective Height and Slenderness Ratio
const he = k * h;
const sr = he / t;

// 3. Basic Allowable Stresses (without slenderness/eccentricity effects)
const Fa_basic = FA_FACTOR * fm;
const Fb_basic = FB_FACTOR * fm; // Allowable bending stress is often a fixed fraction of f'm

// 4. Slenderness Reduction Factor (Ce) for axial capacity
let Ce;
if (sr <= SR_CODE_TRANSITION) {
    Ce = 1 - Math.pow(sr / CE_CONSTANT_1, 2);
} else {
    Ce = Math.pow(CE_CONSTANT_2 / sr, 2);
}
// Cap Ce at 0; capacity cannot be negative
if (Ce < 0) Ce = 0;

// 5. Allowable Axial Compressive Stress (Fa)
const Fa = Fa_basic * Ce;

// 6. Applied Stresses
// Pa is in kN/m. t is in mm.
// fa = Pa [kN/m] * 1000 [N/kN] / (t [mm] * 1000 [mm/m]) = Pa / t [N/mm^2 = MPa]
const fa = Pa / t;

// Ma is in kNm/m. t is in mm.
// fb = Ma [kNm/m] * 10^6 [Nmm/kNm] / ((1/6) * 1000 [mm/m] * t^2 [mm^2])
//     = Ma * 1000 * 6 / t^2 [N/mm^2 = MPa]
const fb = (6 * Ma * 1000) / (t * t);

// 7. Interaction Ratio and Status
let status = "Pass";
let interactionRatio = 0;
let axialRatio = 0;
let bendingRatio = 0;

if (sr > SR_UNREINFORCED_LIMIT) {
    status = "Fail (Slenderness Ratio exceeds common unreinforced masonry limits)";
} else {
    // Calculate axial ratio, handling division by zero for Fa
    if (Fa <= 0) {
        if (fa > 0) { 
            status = "Fail (No axial capacity for applied axial load)";
        }
        axialRatio = fa > 0 ? Infinity : 0; // If capacity is 0 but load is >0, ratio is infinite. Else, 0.
    } else {
        axialRatio = fa / Fa;
    }

    // Calculate bending ratio, handling division by zero for Fb_basic
    if (Fb_basic <= 0) { 
        if (fb > 0) {
            status = "Fail (No bending capacity for applied bending moment)";
        }
        bendingRatio = fb > 0 ? Infinity : 0;
    } else {
        bendingRatio = fb / Fb_basic;
    }

    // Only proceed with interaction ratio check if not already failed due to capacity issues
    if (status === "Pass") {
        interactionRatio = axialRatio + bendingRatio;
        if (interactionRatio > 1.0) {
            status = "Fail (Interaction Ratio Exceeded)";
        }
    }
}

return {
    effective_height: he,
    slenderness_ratio: sr,
    allowable_axial_comp_stress: Fa,
    applied_axial_comp_stress: fa,
    applied_bending_stress: fb,
    interaction_ratio: interactionRatio,
    status: status
};
