function (inputs) {
    // Define base rates per casualty (per 1 'effective casualty')
    const BASE_PERSONNEL_RATES = {
        doctors: 0.05, // 1 per 20
        nurses: 0.1,   // 1 per 10
        paramedics: 0.15, // 1 per ~7
        supportStaff: 0.2 // 1 per 5
    };

    const BASE_EQUIPMENT_RATES = {
        majorTraumaKits: 0.2, // 1 per 5
        burnCareKits: 0.05,   // 1 per 20
        respiratorySupportUnits: 0.1, // 1 per 10
        ivFluidLiters: 1.0,   // 1 liter per casualty
        bloodUnitsRequired: 0.2, // 1 unit per 5 casualties
        transportVehicles: 0.02, // 1 per 50
        fieldTriageStations: 0.01 // 1 per 100 (represents a fixed point for initial setup)
    };

    // Validate inputs to ensure non-negative values
    for (const key in inputs) {
        if (typeof inputs[key] === 'number' && inputs[key] < 0) {
            inputs[key] = 0; // Treat negative numbers as zero for calculations
        }
    }

    let effectiveCasualties = inputs.estimatedCasualties;

    // Ensure a minimum base for planning, even with minor incidents or zero initial estimate for calculation stability.
    // A small number of casualties still requires a response.
    effectiveCasualties = Math.max(effectiveCasualties, inputs.estimatedCasualties > 0 ? 5 : 0); 

    // 1. Adjust for Incident Severity
    let severityMultiplier = 1.0;
    switch (inputs.incidentSeverity) {
        case 'Catastrophic': severityMultiplier = 2.0; break;
        case 'Major': severityMultiplier = 1.5; break;
        case 'Moderate': severityMultiplier = 1.0; break;
        case 'Minor': severityMultiplier = 0.75; break;
        default: severityMultiplier = 1.0;
    }
    effectiveCasualties *= severityMultiplier;

    // Initialize resources based on effective casualties
    let resources = {
        requiredDoctors: 0, requiredNurses: 0, requiredParamedics: 0, requiredSupportStaff: 0,
        majorTraumaKits: 0, burnCareKits: 0, respiratorySupportUnits: 0, ivFluidLiters: 0,
        bloodUnitsRequired: 0, transportVehicles: 0, fieldTriageStations: 0
    };

    for (const key in BASE_PERSONNEL_RATES) {
        resources['required' + key.charAt(0).toUpperCase() + key.slice(1)] = effectiveCasualties * BASE_PERSONNEL_RATES[key];
    }
    for (const key in BASE_EQUIPMENT_RATES) {
        resources[key] = effectiveCasualties * BASE_EQUIPMENT_RATES[key];
    }

    // 2. Adjust for Predominant Injury Profile
    if (inputs.injuryProfile === 'Trauma') {
        resources.majorTraumaKits *= 2.5;
        resources.bloodUnitsRequired *= 2.0;
        resources.ivFluidLiters *= 1.5;
        resources.requiredDoctors += effectiveCasualties * 0.02; // Additional trauma specialists
        resources.requiredNurses += effectiveCasualties * 0.03; 
    } else if (inputs.injuryProfile === 'Burns') {
        resources.burnCareKits *= 3.0;
        resources.ivFluidLiters *= 3.0; // Burns require massive fluid resuscitation
        resources.respiratorySupportUnits *= 1.5;
        resources.requiredDoctors += effectiveCasualties * 0.03; // Additional burn specialists
        resources.requiredNurses += effectiveCasualties * 0.04;
    } else if (inputs.injuryProfile === 'Respiratory') {
        resources.respiratorySupportUnits *= 3.0;
        resources.ivFluidLiters *= 1.2;
        resources.requiredNurses += effectiveCasualties * 0.02;
        resources.requiredSupportStaff += effectiveCasualties * 0.02;
    }
    // 'Mixed' profile uses initial base rates, which are a generalized mix.

    // 3. Adjust for Environmental Factors (Population Density, Distance to Hospital, Geographic Accessibility)
    let envPersonnelMultiplier = 1.0;
    let envLogisticsMultiplier = 1.0;

    // Population Density: Higher density means more complex scene management, potentially more dispersed casualties.
    // Lower density can mean longer search and rescue times.
    // Let's model higher density as slightly increasing complexity for all resources.
    if (inputs.populationDensity > 1000) { // Very Dense Urban
        envPersonnelMultiplier *= 1.2;
        envLogisticsMultiplier *= 1.3; 
    } else if (inputs.populationDensity > 500) { // Dense Urban
        envPersonnelMultiplier *= 1.15;
        envLogisticsMultiplier *= 1.2;
    } else if (inputs.populationDensity > 100) { // Suburban
        envPersonnelMultiplier *= 1.05;
        envLogisticsMultiplier *= 1.05;
    }

    // Distance to Hospital: Longer distance requires more on-site stabilization and extended transport capabilities.
    if (inputs.distanceToHospital > 50) { // Very Remote
        envPersonnelMultiplier *= 1.4;
        envLogisticsMultiplier *= 2.5; 
        resources.ivFluidLiters *= 1.8;
        resources.bloodUnitsRequired *= 1.5;
    } else if (inputs.distanceToHospital > 20) { // Remote/Rural
        envPersonnelMultiplier *= 1.25;
        envLogisticsMultiplier *= 1.8;
        resources.ivFluidLiters *= 1.4;
    } else if (inputs.distanceToHospital > 5) { // Semi-rural/Suburban fringe
        envPersonnelMultiplier *= 1.1;
        envLogisticsMultiplier *= 1.2;
    }

    // Geographic Accessibility: Directly impacts personnel and logistical difficulty.
    switch (inputs.geographicAccessibility) {
        case 'Remote':
            envPersonnelMultiplier *= 1.6;
            envLogisticsMultiplier *= 3.0; // Extreme logistical challenge
            break;
        case 'Rural':
            envPersonnelMultiplier *= 1.3;
            envLogisticsMultiplier *= 2.0;
            break;
        case 'Suburban':
            envPersonnelMultiplier *= 1.05;
            envLogisticsMultiplier *= 1.1;
            break;
        case 'Urban':
        default:
            // Handled by population density for most urban specifics
            break;
    }

    // Apply environmental multipliers to relevant resources
    resources.requiredDoctors *= envPersonnelMultiplier;
    resources.requiredNurses *= envPersonnelMultiplier;
    resources.requiredParamedics *= envPersonnelMultiplier * 1.3; // Paramedics even more crucial for remote/difficult access
    resources.requiredSupportStaff *= envPersonnelMultiplier;
    resources.transportVehicles *= envLogisticsMultiplier;
    resources.fieldTriageStations *= envLogisticsMultiplier;

    // 4. Adjust for Local Medical Capacity
    // Lower capacity means more external resources are needed.
    // Factor: If 100% available, factor is 1. If 50% available, factor is 2. Capped at a sensible maximum.
    let capacityFactor = Math.max(1, 100 / Math.max(1, inputs.localMedicalCapacity)); 
    capacityFactor = Math.min(capacityFactor, 5.0); // Cap capacity factor to prevent unrealistic spikes

    for (const key in resources) {
        // Field stations are for on-site deployment, not directly offset by existing hospital capacity
        if (key !== 'fieldTriageStations') {
            resources[key] *= capacityFactor;
        }
    }

    // 5. Adjust for Incident Duration (for sustained operations requiring rotation and replenishment)
    if (inputs.incidentDurationHours > 0) {
        // Every 12-24 hours adds a percentage for personnel rotation, supply replenishment.
        let durationMultiplier = 1.0 + (inputs.incidentDurationHours / 24) * 0.75; // 75% increase per day for sustained ops
        durationMultiplier = Math.min(durationMultiplier, 4.0); // Cap at 4x for very long incidents (e.g., multi-day)

        for (const key in BASE_PERSONNEL_RATES) { // Personnel need rotation for sustained operations
            resources['required' + key.charAt(0).toUpperCase() + key.slice(1)] *= durationMultiplier;
        }
        // Supplies and certain equipment also need replenishment/resupply for longer durations
        resources.ivFluidLiters *= durationMultiplier;
        resources.bloodUnitsRequired *= durationMultiplier;
        resources.majorTraumaKits *= (1 + (durationMultiplier - 1) * 0.5); // Kits replenish, but not as quickly as personnel scale up
        resources.burnCareKits *= (1 + (durationMultiplier - 1) * 0.5);
        resources.respiratorySupportUnits *= (1 + (durationMultiplier - 1) * 0.5);
    }

    // Apply minimums and ensure all resources are positive integers
    for (const key in resources) {
        resources[key] = Math.max(0, Math.ceil(resources[key]));
    }

    // Establish absolute minimums for critical items if any casualties are estimated
    if (inputs.estimatedCasualties > 0) {
        resources.requiredDoctors = Math.max(resources.requiredDoctors, 1);
        resources.requiredNurses = Math.max(resources.requiredNurses, 2);
        resources.requiredParamedics = Math.max(resources.requiredParamedics, 3);
        resources.transportVehicles = Math.max(resources.transportVehicles, 1);
        resources.fieldTriageStations = Math.max(resources.fieldTriageStations, inputs.estimatedCasualties > 10 ? 1 : 0);
    }

    return {
        requiredDoctors: resources.requiredDoctors,
        requiredNurses: resources.requiredNurses,
        requiredParamedics: resources.requiredParamedics,
        requiredSupportStaff: resources.requiredSupportStaff,
        majorTraumaKits: resources.majorTraumaKits,
        burnCareKits: resources.burnCareKits,
        respiratorySupportUnits: resources.respiratorySupportUnits,
        ivFluidLiters: resources.ivFluidLiters,
        bloodUnitsRequired: resources.bloodUnitsRequired,
        transportVehicles: resources.transportVehicles,
        fieldTriageStations: resources.fieldTriageStations
    };
}
