function calculateDosage(inputs) {
  let thc_mg_per_kg_factor = 0;
  let cbd_mg_per_kg_factor = 0;

  // Base dosage ranges (mg/kg) for starting estimates, based on desired effect
  // These are general starting points and highly variable by individual and condition.
  const THC_BASE_MG_PER_KG_MILD = 0.05; // e.g., 3.5mg for 70kg
  const THC_BASE_MG_PER_KG_MODERATE = 0.1; // e.g., 7mg for 70kg
  const THC_BASE_MG_PER_KG_STRONG = 0.15; // e.g., 10.5mg for 70kg

  const CBD_BASE_MG_PER_KG_MILD = 0.2; // e.g., 14mg for 70kg
  const CBD_BASE_MG_PER_KG_MODERATE = 0.4; // e.g., 28mg for 70kg
  const CBD_BASE_MG_PER_KG_STRONG = 0.6; // e.g., 42mg for 70kg

  // Max single dose caps to prevent excessive psychoactive or sedating effects for common medical use
  const MAX_SINGLE_THC_DOSE_MG = 30; // Typically, doses above 25-30mg THC can be very potent
  const MAX_SINGLE_CBD_DOSE_MG = 100; // CBD can be taken in higher doses, but this is a reasonable cap for single estimation

  switch (inputs.desired_effect_level) {
    case 'Mild':
      thc_mg_per_kg_factor = THC_BASE_MG_PER_KG_MILD;
      cbd_mg_per_kg_factor = CBD_BASE_MG_PER_KG_MILD;
      break;
    case 'Moderate':
      thc_mg_per_kg_factor = THC_BASE_MG_PER_KG_MODERATE;
      cbd_mg_per_kg_factor = CBD_BASE_MG_PER_KG_MODERATE;
      break;
    case 'Strong':
      thc_mg_per_kg_factor = THC_BASE_MG_PER_KG_STRONG;
      cbd_mg_per_kg_factor = CBD_BASE_MG_PER_KG_STRONG;
      break;
    default:
      thc_mg_per_kg_factor = THC_BASE_MG_PER_KG_MILD;
      cbd_mg_per_kg_factor = CBD_BASE_MG_PER_KG_MILD;
  }

  // Calculate target mg based on weight and desired effect, applying caps
  let target_thc_mg = inputs.body_weight_kg * thc_mg_per_kg_factor;
  target_thc_mg = Math.min(target_thc_mg, MAX_SINGLE_THC_DOSE_MG);

  let target_cbd_mg = inputs.body_weight_kg * cbd_mg_per_kg_factor;
  target_cbd_mg = Math.min(target_cbd_mg, MAX_SINGLE_CBD_DOSE_MG);

  // Convert product percentage to mg/ml (assuming 1% concentration = 10 mg/ml for liquid products, a common approximation)
  const product_thc_mg_per_ml = inputs.product_thc_percentage * 10;
  const product_cbd_mg_per_ml = inputs.product_cbd_percentage * 10;

  let recommended_dose_ml = 0;

  // Prioritize THC dosage for calculating volume if THC is present and desired.
  // This approach is often used because THC's psychoactive effects require careful titration.
  if (target_thc_mg > 0 && product_thc_mg_per_ml > 0) {
    recommended_dose_ml = target_thc_mg / product_thc_mg_per_ml;
  } else if (target_cbd_mg > 0 && product_cbd_mg_per_ml > 0) {
    // If no THC target or product has no THC, but CBD is desired and present, calculate based on CBD.
    recommended_dose_ml = target_cbd_mg / product_cbd_mg_per_ml;
  }

  // Ensure recommended volume does not exceed the total bottle volume if provided
  if (inputs.product_bottle_volume_ml && recommended_dose_ml > inputs.product_bottle_volume_ml) {
    recommended_dose_ml = inputs.product_bottle_volume_ml;
  }

  // Calculate the actual THC and CBD received at the recommended volume
  const actual_thc_mg_at_dose = recommended_dose_ml * product_thc_mg_per_ml;
  const actual_cbd_mg_at_dose = recommended_dose_ml * product_cbd_mg_per_ml;

  return {
    recommended_thc_mg: actual_thc_mg_at_dose,
    recommended_cbd_mg: actual_cbd_mg_at_dose,
    recommended_dose_ml: recommended_dose_ml
  };
}
