const { medicationsList, healthConditions, numPrescriptions, numOTCSupplements, age, liverKidneyImpairment } = inputs;

let riskScore = 0;

// Base risk for taking any medication
riskScore += 5;

// Risk from number of medications (prescription and OTC/supplements)
// Capped to prevent disproportionate influence from extremely high numbers
riskScore += Math.min(numPrescriptions * 3.5, 35);
riskScore += Math.min(numOTCSupplements * 2.5, 20);

// Risk from age
// Increased risk for older adults due to altered metabolism and polypharmacy
if (age >= 65) {
    riskScore += Math.min((age - 65) * 0.75, 15); // Moderate increase per year over 65
}
if (age >= 75) {
    riskScore += Math.min((age - 75) * 1.5, 10);  // Further accelerated increase per year over 75
}

// Risk from liver/kidney impairment
const hasImpairment = typeof liverKidneyImpairment === 'string' && liverKidneyImpairment.toLowerCase().includes('yes');
const isUnsureImpairment = typeof liverKidneyImpairment === 'string' && liverKidneyImpairment.toLowerCase().includes('unsure');

if (hasImpairment) {
    riskScore += 25; // Significant increase for diagnosed impairment
} else if (isUnsureImpairment) {
    riskScore += 10; // Moderate increase for uncertainty, prompting caution
}

// Proxy risk based on the perceived complexity of provided text inputs
// (A real assessor would parse these for specific interactions)
if (medicationsList && medicationsList.length > 50) { // If a substantial list is provided
    riskScore += 4;
}
if (healthConditions && healthConditions.length > 30) { // If several conditions are mentioned
    riskScore += 3;
}

// Final score scaling and capping to a maximum of 100
let overallRiskScore = Math.min(Math.max(Math.round(riskScore), 0), 100);

let riskCategory;
if (overallRiskScore >= 75) {
    riskCategory = "Very High Risk";
} else if (overallRiskScore >= 50) {
    riskCategory = "High Risk";
} else if (overallRiskScore >= 25) {
    riskCategory = "Moderate Risk";
} else {
    riskCategory = "Low Risk";
}

return {
    overallRiskScore,
    riskCategory
};
