function calculate({ childWeight, dosePerKg, maxSingleDose, concentration }) {
  if (childWeight <= 0 || dosePerKg <= 0 || maxSingleDose <= 0 || concentration <= 0) {
    return { 
      calculatedDoseMg: NaN, 
      volumeToAdminister: NaN,
      error: 'All inputs must be positive values.'
    };
  }

  // Step 1: Calculate the dose based on weight
  let doseBasedOnWeight = childWeight * dosePerKg;

  // Step 2: Apply the maximum single dose cap
  let finalDoseMg = Math.min(doseBasedOnWeight, maxSingleDose);

  // Step 3: Calculate the volume to administer based on concentration
  let volumeToAdminister = finalDoseMg / concentration;

  return {
    calculatedDoseMg: finalDoseMg,
    volumeToAdminister: volumeToAdminister
  };
}
