/**
 * Calculates a community's mental health resource accessibility score.
 * @param {object} inputs - The input values from the form.
 * @param {number} inputs.numPractitionersPer100k - Licensed Mental Health Practitioners per 100,000 Residents.
 * @param {number} inputs.avgTravelTimeMinutes - Average Travel Time (minutes) to Nearest Mental Health Facility.
 * @param {number} inputs.percentInsuredForMH - Percentage of Population with Adequate Mental Health Insurance Coverage.
 * @param {number} inputs.publicTransportScore - Public Transport Access Score (1=Very Poor, 5=Excellent).
 * @param {number} inputs.povertyRate - Community Poverty Rate (Percentage).
 * @param {number} inputs.ruralityFactor - Rurality Factor (1.0 Urban, 0.75 Suburban, 0.5 Rural).
 * @returns {object} An object containing the overall accessibility index and its components.
 */
function calculateAccessibilityIndex(inputs) {
  const { 
    numPractitionersPer100k,
    avgTravelTimeMinutes,
    percentInsuredForMH,
    publicTransportScore,
    povertyRate,
    ruralityFactor
  } = inputs;

  // Constants for scoring normalization (adjust based on research/expert opinion)
  const PRACTITIONER_IDEAL = 50; // practitioners per 100k for max score (25 points)
  const TRAVEL_TIME_POOR_THRESHOLD = 60; // minutes, beyond which score is 0
  const INSURANCE_IDEAL = 100; // percent, for max score (25 points)
  const TRANSPORT_MAX_LEVEL = 5; // max score for publicTransport (25 points, if 5)
  const POVERTY_MAX_DEDUCTION_FACTOR = 0.30; // Max 30% deduction for extreme poverty (100% poverty rate)

  // Input validation and default to 0 or sane min if NaN/null for robustness
  const nPractitioners = isNaN(numPractitionersPer100k) ? 0 : Math.max(0, numPractitionersPer100k);
  const nTravelTime = isNaN(avgTravelTimeMinutes) ? 0 : Math.max(0, avgTravelTimeMinutes);
  const nInsured = isNaN(percentInsuredForMH) ? 0 : Math.min(100, Math.max(0, percentInsuredForMH));
  const nTransport = isNaN(publicTransportScore) ? 1 : Math.min(TRANSPORT_MAX_LEVEL, Math.max(1, publicTransportScore));
  const nPoverty = isNaN(povertyRate) ? 0 : Math.min(100, Math.max(0, povertyRate));
  const nRurality = isNaN(ruralityFactor) ? 1.0 : Math.min(1.0, Math.max(0.1, ruralityFactor));

  // 1. Calculate Practitioner Availability Component Score (out of 25)
  // Linear scaling, capped at 25 points based on PRACTITIONER_IDEAL
  const practitionerAvailabilityScore = Math.min(25, (nPractitioners / PRACTITIONER_IDEAL) * 25);

  // 2. Calculate Facility Proximity Component Score (out of 25)
  // Inverse linear scaling: 0 min -> 25 points, TRAVEL_TIME_POOR_THRESHOLD min -> 0 points
  const facilityProximityScore = Math.max(0, 25 - (nTravelTime / TRAVEL_TIME_POOR_THRESHOLD) * 25);

  // 3. Calculate Insurance Coverage Component Score (out of 25)
  // Linear scaling: 0% -> 0 points, 100% -> 25 points
  const insuranceCoverageScore = (nInsured / INSURANCE_IDEAL) * 25;

  // 4. Calculate Public Transport Access Component Score (out of 25)
  // Scale 1-5 score to 0-25 range: (1 -> 0, 5 -> 25)
  const transportAccessScore = ((nTransport - 1) / (TRANSPORT_MAX_LEVEL - 1)) * 25;

  // Sum the component scores to get a preliminary base score (max 100)
  let baseScore = 
    practitionerAvailabilityScore +
    facilityProximityScore +
    insuranceCoverageScore +
    transportAccessScore;

  // 5. Apply Poverty Rate Adjustment
  // Higher poverty rate leads to a deduction. Max deduction applied at 100% poverty.
  const povertyDeductionMultiplier = 1 - ((nPoverty / 100) * POVERTY_MAX_DEDUCTION_FACTOR);
  let adjustedScoreAfterPoverty = baseScore * povertyDeductionMultiplier;

  // 6. Apply Rurality Factor Adjustment
  // The rurality factor directly scales the score; lower factor for more rural areas.
  let overallAccessibilityIndex = adjustedScoreAfterPoverty * nRurality;

  // Ensure the final index is within [0, 100]
  overallAccessibilityIndex = Math.max(0, Math.min(100, overallAccessibilityIndex));

  return {
    overallAccessibilityIndex: overallAccessibilityIndex,
    practitionerAvailabilityScore: practitionerAvailabilityScore,
    facilityProximityScore: facilityProximityScore,
    insuranceCoverageScore: insuranceCoverageScore,
    transportAccessScore: transportAccessScore
  };
}
