function calculate({ employeeCount, avgAnnualSalary, currentAbsenteeismRate, targetAbsenteeismReduction, currentProductivityLoss, targetProductivityGain, avgAnnualHealthcareCost, targetHealthcareCostReduction, programCostPerEmployee, programDurationYears }) {

    // Input validation and normalization
    employeeCount = Math.max(0, employeeCount || 0);
    avgAnnualSalary = Math.max(0, avgAnnualSalary || 0);
    currentAbsenteeismRate = Math.max(0, Math.min(100, currentAbsenteeismRate || 0)); // Cap at 100%
    targetAbsenteeismReduction = Math.max(0, Math.min(100, targetAbsenteeismReduction || 0)); // Cap at 100%
    currentProductivityLoss = Math.max(0, Math.min(100, currentProductivityLoss || 0)); // Cap at 100%
    targetProductivityGain = Math.max(0, Math.min(100, targetProductivityGain || 0)); // Cap at 100%
    avgAnnualHealthcareCost = Math.max(0, avgAnnualHealthcareCost || 0);
    targetHealthcareCostReduction = Math.max(0, Math.min(100, targetHealthcareCostReduction || 0)); // Cap at 100%
    programCostPerEmployee = Math.max(0, programCostPerEmployee || 0);
    programDurationYears = Math.max(1, programDurationYears || 1); // Minimum 1 year

    const annualProgramCost = employeeCount * programCostPerEmployee;
    const totalProgramCost = annualProgramCost * programDurationYears;

    // 1. Absenteeism Savings
    const currentAbsenteeismCostPerEmployee = (currentAbsenteeismRate / 100) * avgAnnualSalary;
    const reducedAbsenteeismCostPerEmployee = currentAbsenteeismCostPerEmployee * (targetAbsenteeismReduction / 100);
    const annualAbsenteeismSavings = reducedAbsenteeismCostPerEmployee * employeeCount;
    const absenteeismSavings = annualAbsenteeismSavings * programDurationYears;

    // 2. Productivity Gain Value (from reduced presenteeism)
    const currentProductivityLossValuePerEmployee = (currentProductivityLoss / 100) * avgAnnualSalary;
    const annualProductivityGain = currentProductivityLossValuePerEmployee * (targetProductivityGain / 100) * employeeCount; 
    const productivityGainValue = annualProductivityGain * programDurationYears;

    // 3. Healthcare Cost Savings
    const annualHealthcareCostReduction = avgAnnualHealthcareCost * (targetHealthcareCostReduction / 100);
    const annualTotalHealthcareSavings = annualHealthcareCostReduction * employeeCount;
    const healthcareCostSavings = annualTotalHealthcareSavings * programDurationYears;

    // Ensure all savings are non-negative
    const safeAbsenteeismSavings = Math.max(0, absenteeismSavings);
    const safeProductivityGainValue = Math.max(0, productivityGainValue);
    const safeHealthcareCostSavings = Math.max(0, healthcareCostSavings);

    const totalBenefits = safeAbsenteeismSavings + safeProductivityGainValue + safeHealthcareCostSavings;
    const netBenefit = totalBenefits - totalProgramCost;

    let roiPercentage = 0;
    if (totalProgramCost > 0) {
        roiPercentage = (netBenefit / totalProgramCost) * 100;
    } else if (totalBenefits > 0) {
        roiPercentage = Infinity; // Infinite ROI if cost is zero and benefits exist
    } else {
        roiPercentage = 0; // No benefits, no cost, no ROI
    }

    return {
        totalProgramCost: totalProgramCost,
        absenteeismSavings: safeAbsenteeismSavings,
        productivityGainValue: safeProductivityGainValue,
        healthcareCostSavings: safeHealthcareCostSavings,
        totalBenefits: totalBenefits,
        netBenefit: netBenefit,
        roiPercentage: roiPercentage
    };
}
