function calculateMicrogridIndependence(inputs) {
    const {
        avgDailyConsumption,
        solarPVCapacity,
        solarPeakSunHours,
        windTurbineCapacity,
        windCapacityFactor,
        batteryStorageCapacity,
        batteryEfficiency,
        criticalLoadPercentage,
        desiredAutonomyDays
    } = inputs;

    const safeAvgDailyConsumption = Math.max(0, avgDailyConsumption);
    const safeSolarPVCapacity = Math.max(0, solarPVCapacity);
    const safeSolarPeakSunHours = Math.max(0, solarPeakSunHours);
    const safeWindTurbineCapacity = Math.max(0, windTurbineCapacity);
    const safeWindCapacityFactor = Math.max(0, Math.min(100, windCapacityFactor));
    const safeBatteryStorageCapacity = Math.max(0, batteryStorageCapacity);
    const safeBatteryEfficiency = Math.max(0, Math.min(100, batteryEfficiency));
    const safeCriticalLoadPercentage = Math.max(0, Math.min(100, criticalLoadPercentage));
    const safeDesiredAutonomyDays = Math.max(0, desiredAutonomyDays);

    let energyIndependencePercentage = 0;
    let autonomyDaysAchieved = 0;
    let storageDeficit = 0;
    let generationSurplus = 0;

    // 1. Calculate Total Daily Generation (kWh/day)
    const solarGeneration = safeSolarPVCapacity * safeSolarPeakSunHours;
    const windGeneration = safeWindTurbineCapacity * 24 * (safeWindCapacityFactor / 100);
    const totalDailyGeneration = solarGeneration + windGeneration;

    // 2. Calculate Effective Daily Critical Load (kWh/day)
    const effectiveCriticalLoad = safeAvgDailyConsumption * (safeCriticalLoadPercentage / 100);

    // Handle edge cases for independence % and autonomy days
    if (safeAvgDailyConsumption === 0 || effectiveCriticalLoad === 0 || safeDesiredAutonomyDays === 0) {
        energyIndependencePercentage = 100;
        autonomyDaysAchieved = 365; // Effectively infinite
        storageDeficit = 0;
        generationSurplus = Math.max(0, totalDailyGeneration - safeAvgDailyConsumption);
        return {
            energyIndependencePercentage: parseFloat(energyIndependencePercentage.toFixed(1)),
            autonomyDaysAchieved: parseFloat(autonomyDaysAchieved.toFixed(1)),
            storageDeficit: parseFloat(storageDeficit.toFixed(1)),
            generationSurplus: parseFloat(generationSurplus.toFixed(1))
        };
    }

    // 3. Calculate Net Load to be Met by Storage (kWh/day) for critical loads
    const netLoadForStorageDaily = Math.max(0, effectiveCriticalLoad - totalDailyGeneration);

    // 4. Calculate Achieved Autonomy Days
    const usableBatteryEnergy = safeBatteryStorageCapacity * (safeBatteryEfficiency / 100);

    if (netLoadForStorageDaily === 0) {
        autonomyDaysAchieved = 365; // Capped for practical display if generation meets/exceeds critical load
    } else {
        autonomyDaysAchieved = usableBatteryEnergy / netLoadForStorageDaily;
    }

    // 5. Calculate Energy Independence Percentage
    const totalEnergyNeededForAutonomy = effectiveCriticalLoad * safeDesiredAutonomyDays;
    const energyFromGenerationDuringAutonomy = totalDailyGeneration * safeDesiredAutonomyDays;

    const totalEnergySuppliedLocally = energyFromGenerationDuringAutonomy + usableBatteryEnergy;

    energyIndependencePercentage = Math.min(100, (totalEnergySuppliedLocally / totalEnergyNeededForAutonomy) * 100);

    // 6. Calculate Storage Deficit
    if (autonomyDaysAchieved < safeDesiredAutonomyDays) {
        const energyRequiredBeyondSupply = totalEnergyNeededForAutonomy - totalEnergySuppliedLocally;
        storageDeficit = Math.max(0, energyRequiredBeyondSupply);
    } else {
        storageDeficit = 0;
    }

    // 7. Calculate Daily Generation Surplus
    generationSurplus = Math.max(0, totalDailyGeneration - safeAvgDailyConsumption);

    return {
        energyIndependencePercentage: parseFloat(energyIndependencePercentage.toFixed(1)),
        autonomyDaysAchieved: parseFloat(autonomyDaysAchieved.toFixed(1)),
        storageDeficit: parseFloat(storageDeficit.toFixed(1)),
        generationSurplus: parseFloat(generationSurplus.toFixed(1))
    };
}
