const age = inputs.age;
const gender = inputs.gender.toLowerCase();

// Base Recommended Daily Allowances (RDAs) for typical adults
let rda_vitamin_c = 90; // mg (male)
let rda_iron = 8;     // mg (male)
let rda_calcium = 1000; // mg (adults 19-50)

// Adjust RDAs based on gender and age
if (gender === 'female') {
    rda_vitamin_c = 75; // mg for adult females
    rda_iron = (age >= 19 && age <= 50) ? 18 : 8; // 18mg for pre-menopausal, 8mg for post-menopausal
}

if (age >= 51) {
    if (gender === 'female') {
        rda_calcium = 1200; // mg for females 51+ and males 71+
    } else if (age >= 71) {
        rda_calcium = 1200; // mg for males 71+
    } else {
        rda_calcium = 1000; // mg for males 51-70
    }
}

// Calculate intake percentages relative to adjusted RDAs
const vit_c_percent = (inputs.vitamin_c_intake / rda_vitamin_c) * 100;
const iron_percent = (inputs.iron_intake / rda_iron) * 100;
const calcium_percent = (inputs.calcium_intake / rda_calcium) * 100;

// Calculate individual nutrient contributions to the overall score (0-100 for each)
// Penalize deficiencies and significant excesses.

let vit_c_contribution = 0;
if (vit_c_percent >= 90 && vit_c_percent <= 150) {
    vit_c_contribution = 100; // Ideal range
} else if (vit_c_percent > 150 && vit_c_percent <= 300) {
    vit_c_contribution = 100 - ((vit_c_percent - 150) / 150) * 50; // Max 50% penalty for 300%
} else if (vit_c_percent < 90 && vit_c_percent >= 50) {
    vit_c_contribution = 100 - ((90 - vit_c_percent) / 40) * 50; // Max 50% penalty for 50%
} else {
    vit_c_contribution = 0; // Severe deficiency or excess
}
vit_c_contribution = Math.max(0, vit_c_contribution); // Ensure non-negative

let iron_contribution = 0;
// Iron needs tighter control due to potential toxicity in excess.
if (iron_percent >= 80 && iron_percent <= 120) {
    iron_contribution = 100; // Ideal range
} else if (iron_percent > 120 && iron_percent <= 200) {
    iron_contribution = 100 - ((iron_percent - 120) / 80) * 75; // Max 75% penalty for 200%
} else if (iron_percent < 80 && iron_percent >= 40) {
    iron_contribution = 100 - ((80 - iron_percent) / 40) * 75; // Max 75% penalty for 40%
} else {
    iron_contribution = 0; // Severe deficiency or excess
}
iron_contribution = Math.max(0, iron_contribution);

let calcium_contribution = 0;
if (calcium_percent >= 90 && calcium_percent <= 150) {
    calcium_contribution = 100; // Ideal range
} else if (calcium_percent > 150 && calcium_percent <= 250) {
    calcium_contribution = 100 - ((calcium_percent - 150) / 100) * 60; // Max 60% penalty for 250%
} else if (calcium_percent < 90 && calcium_percent >= 50) {
    calcium_contribution = 100 - ((90 - calcium_percent) / 40) * 60; // Max 60% penalty for 50%
} else {
    calcium_contribution = 0; // Severe deficiency or excess
}
calcium_contribution = Math.max(0, calcium_contribution);

// Overall balance score is an average of individual nutrient contributions
const nutrients_count = 3;
let score = (vit_c_contribution + iron_contribution + calcium_contribution) / nutrients_count;
score = Math.max(0, Math.min(100, score)); // Ensure score is between 0 and 100

// Determine overall status message based on the score
let overall_status = '';
if (score >= 90) {
    overall_status = 'Excellent Balance: Your micronutrient intake appears well-balanced.';
} else if (score >= 70) {
    overall_status = 'Good Balance: Minor adjustments may be beneficial to optimize your intake.';
} else if (score >= 50) {
    overall_status = 'Moderate Balance: Significant dietary review recommended to address potential imbalances.';
} else {
    overall_status = 'Poor Balance: Professional dietary guidance is strongly advised to address significant deficiencies or excesses.';
}

return {
    vitamin_c_balance: vit_c_percent,
    iron_balance: iron_percent,
    calcium_balance: calcium_percent,
    overall_balance_score: score,
    overall_balance_status: overall_status
};
