function calculate({ current_monthly_non_essential_spending, target_purchase_reduction_percentage, current_monthly_utility_spend, target_utility_reduction_percentage, current_monthly_housing_cost, target_housing_reduction_percentage, current_monthly_waste_output_kg, target_waste_reduction_percentage, projection_period_months }) {

  // Input validation and clamping
  const c_non_essential = Math.max(0, current_monthly_non_essential_spending);
  const t_purchase_red_percent = Math.min(100, Math.max(0, target_purchase_reduction_percentage));
  const c_utility_spend = Math.max(0, current_monthly_utility_spend);
  const t_utility_red_percent = Math.min(100, Math.max(0, target_utility_reduction_percentage));
  const c_housing_cost = Math.max(0, current_monthly_housing_cost);
  const t_housing_red_percent = Math.min(100, Math.max(0, target_housing_reduction_percentage));
  const c_waste_output_kg = Math.max(0, current_monthly_waste_output_kg);
  const t_waste_red_percent = Math.min(100, Math.max(0, target_waste_reduction_percentage));
  const proj_months = Math.max(0, projection_period_months);

  // Calculate financial savings
  const purchase_savings = c_non_essential * (t_purchase_red_percent / 100);
  const utility_savings = c_utility_spend * (t_utility_red_percent / 100);
  const housing_savings = c_housing_cost * (t_housing_red_percent / 100);

  const projected_monthly_financial_savings = purchase_savings + utility_savings + housing_savings;

  const projected_annual_financial_savings = projected_monthly_financial_savings * 12;

  const total_projected_financial_savings = projected_monthly_financial_savings * proj_months;

  // Calculate waste reduction
  const monthly_waste_reduction_kg = c_waste_output_kg * (t_waste_red_percent / 100);
  const projected_total_waste_reduction_kg = monthly_waste_reduction_kg * proj_months;

  // Calculate environmental impact score
  // A simple average of the two relevant percentages, scaled to 100.
  // Max score for 100% reduction in both categories.
  const environmental_impact_score = Math.round(
    ((t_utility_red_percent + t_waste_red_percent) / 200) * 100
  );

  return {
    projected_monthly_financial_savings,
    projected_annual_financial_savings,
    total_projected_financial_savings,
    projected_total_waste_reduction_kg,
    environmental_impact_score
  };
}
