formula:
let baseRisk = 40; // Starting point for risk

// Adjust risk based on inputs
let score = baseRisk;

// Factor 1: Apps with high-risk permissions (more apps = higher risk)
score += inputs.numHighRiskPermissionsApps * 2.5; // Each app adds 2.5 points

// Factor 2: Location service usage (higher usage = higher risk)
// 0=Never (no change), 1=Rarely (+5), 2=Often (+15), 3=Always (+25)
score += inputs.locationServiceUsage === 1 ? 5 : inputs.locationServiceUsage === 2 ? 15 : inputs.locationServiceUsage === 3 ? 25 : 0;

// Factor 3: Device security features (better features = lower risk)
// 0=None (+15), 1=Basic (+5), 2=Good (-5), 3=Excellent (-15)
score -= inputs.deviceSecurityFeatures === 0 ? -15 : inputs.deviceSecurityFeatures === 1 ? -5 : inputs.deviceSecurityFeatures === 2 ? 5 : inputs.deviceSecurityFeatures === 3 ? 15 : 0;

// Factor 4: Data sharing practices (more sharing = higher risk)
// 0=Never (no change), 1=Rarely (+5), 2=Sometimes (+15), 3=Often (+25)
score += inputs.dataSharingPractices === 1 ? 5 : inputs.dataSharingPractices === 2 ? 15 : inputs.dataSharingPractices === 3 ? 25 : 0;

// Factor 5: OS update status (outdated = higher risk)
// 0=Outdated (+20), 1=Recent (+5), 2=Current (-5)
score += inputs.osUpdateStatus === 0 ? 20 : inputs.osUpdateStatus === 1 ? 5 : inputs.osUpdateStatus === 2 ? -5 : 0;

// Factor 6: App installer source (unofficial = higher risk)
// 0=Never (no change), 1=Rarely (+10), 2=Often (+30)
score += inputs.appInstallerSource === 1 ? 10 : inputs.appInstallerSource === 2 ? 30 : 0;

// Cap the score between 0 and 100
score = Math.max(0, Math.min(100, score));

let recommendationLevel;
if (score <= 25) {
    recommendationLevel = "Excellent: Your mobile data privacy is very strong. Keep up the great work!";
} else if (score <= 50) {
    recommendationLevel = "Good: Your privacy is generally well-protected, but minor improvements are possible.";
} else if (score <= 75) {
    recommendationLevel = "Moderate: Your mobile privacy has some vulnerabilities. Significant actions are recommended.";
} else {
    recommendationLevel = "High: Your mobile data privacy is at significant risk. Immediate and decisive action is required.";
}

return {
    privacyRiskScore: Math.round(score),
    recommendationLevel: recommendationLevel
};
