function calculateROI(inputs) {
    const {
        project_area_sqft,
        traditional_cost_per_sqft,
        modular_material_cost_savings_pct,
        modular_labor_cost_savings_pct,
        project_duration_traditional_months,
        modular_time_reduction_pct,
        opportunity_cost_per_month,
        financing_interest_rate_annual_pct,
        traditional_defect_rate_pct,
        modular_defect_reduction_pct
    } = inputs;

    // Validate inputs to ensure they are numbers and meet minimum requirements
    const validate = (val, min = 0, fieldName) => {
        if (typeof val !== 'number' || isNaN(val) || val < min) {
            throw new Error(`Invalid input for ${fieldName}. Must be a number greater than or equal to ${min}.`);
        }
        return val;
    };

    try {
        const area = validate(project_area_sqft, 1, 'Project Total Area');
        const tradCostSqft = validate(traditional_cost_per_sqft, 0.01, 'Traditional Construction Cost Per Sq. Ft.');
        const matSavingsPct = Math.max(0, validate(modular_material_cost_savings_pct, 0, 'Modular Material Cost Savings %'));
        const labSavingsPct = Math.max(0, validate(modular_labor_cost_savings_pct, 0, 'Modular On-Site Labor Cost Savings %'));
        const tradDurationMonths = validate(project_duration_traditional_months, 0.1, 'Traditional Project Duration (Months)');
        const timeReductionPct = Math.max(0, validate(modular_time_reduction_pct, 0, 'Modular Project Time Reduction %'));
        const oppCostMonthly = validate(opportunity_cost_per_month, 0, 'Monthly Revenue/Cost Avoidance (Opportunity Cost)');
        const annualInterestRate = validate(financing_interest_rate_annual_pct, 0, 'Annual Project Financing Interest Rate %');
        const tradDefectRate = Math.max(0, validate(traditional_defect_rate_pct, 0, 'Traditional Construction Defect Rate %'));
        const modDefectReduction = Math.max(0, validate(modular_defect_reduction_pct, 0, 'Modular Defect Rate Reduction %'));

        // --- Step 1: Calculate Base Costs for Traditional and Modular Construction --- 
        // Traditional Base Construction Cost (Materials, Labor, Overhead - before financing/defects)
        const traditional_base_cost_initial = area * tradCostSqft;

        // Assumed Cost Component Split for applying specific modular savings
        // These percentages are internal assumptions for the calculation logic to distribute savings
        const MATERIAL_WEIGHT = 0.40; // Assumes 40% of traditional_base_cost is materials
        const LABOR_WEIGHT = 0.30;    // Assumes 30% of traditional_base_cost is labor
        const OTHER_WEIGHT = 0.30;    // Assumes 30% of traditional_base_cost is other overhead/profit

        const traditional_material_component = traditional_base_cost_initial * MATERIAL_WEIGHT;
        const traditional_labor_component = traditional_base_cost_initial * LABOR_WEIGHT;
        const traditional_other_component = traditional_base_cost_initial * OTHER_WEIGHT;

        // Modular Base Construction Cost (applying specific savings percentages)
        const modular_material_cost = traditional_material_component * (1 - matSavingsPct / 100);
        const modular_labor_cost = traditional_labor_component * (1 - labSavingsPct / 100);

        // The 'Other' component is generally assumed to be similar unless project-specific details suggest otherwise.
        const modular_base_cost_initial_raw = modular_material_cost + modular_labor_cost + traditional_other_component;

        // Ensure modular cost doesn't go below a reasonable floor (e.g., 10% of traditional base cost)
        const minModularBaseCostFloor = traditional_base_cost_initial * 0.1;
        const modular_base_cost = Math.max(minModularBaseCostFloor, modular_base_cost_initial_raw);


        // --- Step 2: Calculate Project Durations and Time Savings --- 

        // Modular project duration, capped at a minimum to avoid division by zero or unrealistic speeds
        const modular_project_duration_months = Math.max(0.1, tradDurationMonths * (1 - timeReductionPct / 100)); 
        const total_time_savings_months = Math.max(0, tradDurationMonths - modular_project_duration_months);


        // --- Step 3: Calculate Financing Costs --- 
        // Assumes capital is drawn linearly over the project duration, so average capital outstanding is total cost / 2
        const monthly_interest_rate_decimal = (annualInterestRate / 100) / 12;

        const traditional_financing_cost = (traditional_base_cost_initial / 2) * monthly_interest_rate_decimal * tradDurationMonths;
        const modular_financing_cost = (modular_base_cost / 2) * monthly_interest_rate_decimal * modular_project_duration_months;


        // --- Step 4: Calculate Defect Costs --- 

        const traditional_defect_cost = traditional_base_cost_initial * (tradDefectRate / 100);

        // Calculate modular defect rate as a reduction from traditional defect rate
        const modular_actual_defect_rate_pct = tradDefectRate * (1 - modDefectReduction / 100);
        const modular_defect_cost = modular_base_cost * (modular_actual_defect_rate_pct / 100);


        // --- Step 5: Calculate Total Project Costs for Traditional and Modular --- 

        const traditional_total_cost = traditional_base_cost_initial + traditional_financing_cost + traditional_defect_cost;
        const modular_total_cost = modular_base_cost + modular_financing_cost + modular_defect_cost;


        // --- Step 6: Calculate Overall Financial Benefits and ROI --- 

        const total_cost_savings = traditional_total_cost - modular_total_cost;
        const accelerated_revenue_or_cost_avoidance = oppCostMonthly * total_time_savings_months;

        const net_financial_benefit = total_cost_savings + accelerated_revenue_or_cost_avoidance;

        let return_on_investment_pct = 0;
        if (modular_total_cost > 0) { // Avoid division by zero for ROI
            return_on_investment_pct = (net_financial_benefit / modular_total_cost) * 100;
        } else if (net_financial_benefit > 0) {
            return_on_investment_pct = Infinity; // Infinite ROI if modular cost is zero but benefit exists
        }

        let payback_period_months = 'N/A';
        // Payback period is relevant if modular has a higher overall cost but generates sufficient monthly benefits
        if (total_cost_savings < 0) { // If modular is more expensive initially (negative cost savings)
            const additional_modular_investment = Math.abs(total_cost_savings);
            // Monthly benefit rate from accelerated revenue/cost avoidance
            const monthly_benefit_rate = (total_time_savings_months > 0) ? (accelerated_revenue_or_cost_avoidance / total_time_savings_months) : 0;

            if (monthly_benefit_rate > 0) {
                payback_period_months = additional_modular_investment / monthly_benefit_rate;
            } else {
                payback_period_months = 'N/A'; // No recurring monthly benefit to offset additional cost
            }
        } else {
            payback_period_months = 0; // If modular is cheaper or equal, payback is immediate
        }

        return {
            traditional_total_cost,
            modular_total_cost,
            total_cost_savings,
            traditional_project_duration_months: tradDurationMonths,
            modular_project_duration_months,
            total_time_savings_months,
            accelerated_revenue_or_cost_avoidance,
            net_financial_benefit,
            return_on_investment_pct,
            payback_period_months: typeof payback_period_months === 'number' ? payback_period_months : payback_period_months
        };

    } catch (error) {
        // Return structured error message and zeroed outputs for graceful error handling
        return {
            error: error.message,
            traditional_total_cost: 0,
            modular_total_cost: 0,
            total_cost_savings: 0,
            traditional_project_duration_months: 0,
            modular_project_duration_months: 0,
            total_time_savings_months: 0,
            accelerated_revenue_or_cost_avoidance: 0,
            net_financial_benefit: 0,
            return_on_investment_pct: 0,
            payback_period_months: 'N/A'
        };
    }
}
