function calculate({ totalModules, avgFabLeadTimeDays, factoryDailyOutputCapacity, modulesPerShipment, avgRoundTripTransportDaysPerShipment, siteAssemblyRateModulesPerDay, avgDirectMaterialCostPerModule, avgFabricationLaborCostPerModule, avgSiteLaborCostPerModule, overheadContingencyRate }) {

  // Input validation and sanitization for safety
  totalModules = Math.max(1, totalModules);
  avgFabLeadTimeDays = Math.max(0.5, avgFabLeadTimeDays);
  factoryDailyOutputCapacity = Math.max(0.1, factoryDailyOutputCapacity);
  modulesPerShipment = Math.max(1, modulesPerShipment);
  avgRoundTripTransportDaysPerShipment = Math.max(0.1, avgRoundTripTransportDaysPerShipment);
  siteAssemblyRateModulesPerDay = Math.max(0.1, siteAssemblyRateModulesPerDay);
  avgDirectMaterialCostPerModule = Math.max(0, avgDirectMaterialCostPerModule);
  avgFabricationLaborCostPerModule = Math.max(0, avgFabricationLaborCostPerModule);
  avgSiteLaborCostPerModule = Math.max(0, avgSiteLaborCostPerModule);
  overheadContingencyRate = Math.min(100, Math.max(0, overheadContingencyRate));

  // 1. Calculate Total Fabrication Duration
  // The duration starts with the lead time for the first module, then adds the time for subsequent modules
  // based on the factory's daily output capacity.
  const totalFabDuration = avgFabLeadTimeDays + Math.max(0, (totalModules - 1) / factoryDailyOutputCapacity);

  // 2. Calculate Total Transportation Duration
  // Determine the number of shipments needed and multiply by the average round-trip time per shipment.
  // This assumes a serial or critical-path-limited transport schedule.
  const numShipments = Math.ceil(totalModules / modulesPerShipment);
  const totalTransportDuration = numShipments * avgRoundTripTransportDaysPerShipment;

  // 3. Calculate Total On-site Assembly Duration
  // Divide the total number of modules by the daily on-site assembly rate.
  const totalAssemblyDuration = totalModules / siteAssemblyRateModulesPerDay;

  // 4. Calculate Total Project Duration
  // Sum the durations of the three main phases. This assumes a sequential critical path
  // where one phase largely completes before the next significantly begins.
  const totalProjectDurationDays = totalFabDuration + totalTransportDuration + totalAssemblyDuration;

  // 5. Calculate Direct Costs
  const totalMaterialCost = totalModules * avgDirectMaterialCostPerModule;
  const totalFabricationLaborCost = totalModules * avgFabricationLaborCostPerModule;
  const totalSiteLaborCost = totalModules * avgSiteLaborCostPerModule;
  const subtotalDirectCost = totalMaterialCost + totalFabricationLaborCost + totalSiteLaborCost;

  // 6. Calculate Overhead & Contingency Cost
  const totalOverheadContingencyCost = subtotalDirectCost * (overheadContingencyRate / 100);

  // 7. Calculate Total Project Cost
  const totalProjectCostUSD = subtotalDirectCost + totalOverheadContingencyCost;

  // 8. Calculate Average Cost and Days Per Module for insights
  const averageCostPerModule = totalModules > 0 ? totalProjectCostUSD / totalModules : 0;
  const averageDaysPerModule = totalModules > 0 ? totalProjectDurationDays / totalModules : 0;

  return {
    totalProjectDurationDays,
    totalProjectCostUSD,
    averageCostPerModule,
    averageDaysPerModule
  };
}
