const grossMonthlyIncome = inputs.grossMonthlyIncome;
const monthlyDebtPayments = inputs.monthlyDebtPayments;
const downPaymentAmount = inputs.downPaymentAmount;
const annualInterestRate = inputs.annualInterestRate;
const loanTermYears = inputs.loanTermYears;
const annualPropertyTaxRate = inputs.annualPropertyTaxRate;
const annualHomeInsuranceCost = inputs.annualHomeInsuranceCost;

const monthlyInterestRate = annualInterestRate / 100 / 12;
const numberOfPayments = loanTermYears * 12;
const monthlyHomeInsurance = annualHomeInsuranceCost / 12;

// Lenders' common affordability ratios (front-end & back-end DTI)
const frontEndRatio = 0.28; // Max PITI as % of gross income
const backEndRatio = 0.36; // Max (PITI + other debts) as % of gross income

// 1. Calculate maximum PITI based on income and existing debts
const maxPITI_from_front_end = grossMonthlyIncome * frontEndRatio;
const maxPITI_from_back_end = grossMonthlyIncome * backEndRatio - monthlyDebtPayments;

// The maximum PITI must satisfy both ratios. It cannot be negative.
const maxAllowedPITI = Math.max(0, Math.min(maxPITI_from_front_end, maxPITI_from_back_end));

// Special case: If maxAllowedPITI is 0, no loan is affordable.
if (maxAllowedPITI <= 0) {
    return {
        estimatedMonthlyPayment: 0,
        maxAffordableLoanAmount: 0,
        maxAffordableHomeValue: downPaymentAmount
    };
}

// Calculate mortgage constant (P&I payment per dollar of loan)
let mortgageConstant;
if (monthlyInterestRate === 0) {
    mortgageConstant = 1 / numberOfPayments;
} else {
    const factor = Math.pow(1 + monthlyInterestRate, numberOfPayments);
    mortgageConstant = (monthlyInterestRate * factor) / (factor - 1);
}

// Effective monthly property tax rate as a decimal of home value
const effectiveTaxRateMonthly = annualPropertyTaxRate / 100 / 12;

// Solve for LoanAmount (L) from: 
// L * mortgageConstant + effectiveTaxRateMonthly * (L + downPaymentAmount) + monthlyHomeInsurance = maxAllowedPITI
// L * (mortgageConstant + effectiveTaxRateMonthly) = maxAllowedPITI - (effectiveTaxRateMonthly * downPaymentAmount) - monthlyHomeInsurance
const numerator = maxAllowedPITI - (effectiveTaxRateMonthly * downPaymentAmount) - monthlyHomeInsurance;
const denominator = mortgageConstant + effectiveTaxRateMonthly;

let maxAffordableLoanAmount = 0;
if (denominator > 0) {
    maxAffordableLoanAmount = Math.max(0, numerator / denominator);
}

// Calculate the maximum affordable home value
const maxAffordableHomeValue = maxAffordableLoanAmount + downPaymentAmount;

// Calculate the estimated monthly payment for the determined maxAffordableLoanAmount
let principalInterestPayment = 0;
if (maxAffordableLoanAmount > 0) {
    if (monthlyInterestRate === 0) {
        principalInterestPayment = maxAffordableLoanAmount / numberOfPayments;
    } else {
        const factor = Math.pow(1 + monthlyInterestRate, numberOfPayments);
        principalInterestPayment = maxAffordableLoanAmount * (monthlyInterestRate * factor) / (factor - 1);
    }
}

const monthlyPropertyTax = (annualPropertyTaxRate / 100) * maxAffordableHomeValue / 12;
const estimatedMonthlyPayment = principalInterestPayment + monthlyPropertyTax + monthlyHomeInsurance;

// Ensure the final estimated payment does not exceed the calculated maxAllowedPITI
const finalEstimatedMonthlyPayment = Math.min(estimatedMonthlyPayment, maxAllowedPITI);

return {
    estimatedMonthlyPayment: finalEstimatedMonthlyPayment,
    maxAffordableLoanAmount: maxAffordableLoanAmount,
    maxAffordableHomeValue: maxAffordableHomeValue
};
