formula:
(inputs) => {
    const { loanAmount, annualInterestRate, loanTermYears, extraMonthlyPayment } = inputs;

    if (loanAmount <= 0 || annualInterestRate < 0 || loanTermYears <= 0) {
        return { error: "Please enter positive values for loan amount, interest rate, and loan term." };
    }

    const principal = loanAmount;
    const annualRate = annualInterestRate / 100;
    const monthlyRate = annualRate / 12;
    const totalScheduledMonths = loanTermYears * 12;

    let monthlyPayment;
    if (monthlyRate === 0) {
        monthlyPayment = principal / totalScheduledMonths;
    } else {
        // Standard mortgage payment formula: M = P [ i(1 + i)^n ] / [ (1 + i)^n – 1]
        monthlyPayment = principal * (monthlyRate * Math.pow(1 + monthlyRate, totalScheduledMonths)) / (Math.pow(1 + monthlyRate, totalScheduledMonths) - 1);
    }

    let remainingBalance = principal;
    let totalInterestPaid = 0;
    let totalPaymentsSum = 0;
    let currentMonth = 0;

    const schedule = [];
    const effectiveExtraPayment = extraMonthlyPayment || 0;

    // Loop to generate amortization schedule until balance is paid off
    // Cap the loop to prevent infinite calculations in edge cases (e.g., tiny remaining balance)
    while (remainingBalance > 0.01 && currentMonth < (totalScheduledMonths * 2)) { 
        currentMonth++;

        let interestThisMonth = remainingBalance * monthlyRate;

        // The total amount the borrower intends to pay this month
        let intendedPayment = monthlyPayment + effectiveExtraPayment;

        // If the intended payment is more than what's needed to pay off the loan this month
        let actualPaymentThisMonth;
        let principalPaidThisMonth;

        if (intendedPayment >= remainingBalance + interestThisMonth) {
            actualPaymentThisMonth = remainingBalance + interestThisMonth;
            principalPaidThisMonth = remainingBalance;
            remainingBalance = 0;
        } else {
            principalPaidThisMonth = intendedPayment - interestThisMonth;
            remainingBalance -= principalPaidThisMonth;
            actualPaymentThisMonth = intendedPayment;
        }

        totalInterestPaid += interestThisMonth;
        totalPaymentsSum += actualPaymentThisMonth;

        schedule.push({
            month: currentMonth,
            startingBalance: parseFloat((remainingBalance + principalPaidThisMonth).toFixed(2)),
            payment: parseFloat(actualPaymentThisMonth.toFixed(2)),
            interestPaid: parseFloat(interestThisMonth.toFixed(2)),
            principalPaid: parseFloat(principalPaidThisMonth.toFixed(2)),
            endingBalance: parseFloat(Math.max(0, remainingBalance).toFixed(2)) // Ensure balance doesn't go negative
        });
    }

    return {
        monthlyPayment: parseFloat(monthlyPayment.toFixed(2)),
        totalInterestPaid: parseFloat(totalInterestPaid.toFixed(2)),
        totalPaymentsMade: parseFloat(totalPaymentsSum.toFixed(2)),
        loanPaidOffMonths: currentMonth,
        schedule: schedule
    };
}
