const currentLoanBalance = inputs.currentLoanBalance;
const currentMonthlyPayment = inputs.currentMonthlyPayment;
const newInterestRate = inputs.newInterestRate;
const newLoanTermYears = inputs.newLoanTermYears;
const closingCosts = inputs.closingCosts;

if (currentLoanBalance <= 0 || currentMonthlyPayment <= 0 || newInterestRate < 0 || newLoanTermYears <= 0 || closingCosts < 0) {
    return {
        monthlyDifference: 0,
        breakevenMonths: Infinity
    };
}

const newMonthlyRate = (newInterestRate / 100) / 12;
const newTotalMonths = newLoanTermYears * 12;

let newMonthlyPayment;
if (newMonthlyRate === 0) {
    newMonthlyPayment = currentLoanBalance / newTotalMonths;
} else {
    newMonthlyPayment = currentLoanBalance * (newMonthlyRate * Math.pow(1 + newMonthlyRate, newTotalMonths)) / (Math.pow(1 + newMonthlyRate, newTotalMonths) - 1);
}

const monthlyDifference = currentMonthlyPayment - newMonthlyPayment;

let breakevenMonths;
if (monthlyDifference > 0) {
    breakevenMonths = closingCosts / monthlyDifference;
} else {
    breakevenMonths = Infinity;
}

return {
    monthlyDifference: monthlyDifference,
    breakevenMonths: breakevenMonths
};
