function calculate({
  motor_rated_power_kw,
  motor_rated_voltage_v,
  motor_power_factor_pu,
  motor_locked_rotor_current_multiple,
  source_short_circuit_mva,
  xfmr_impedance_percent,
  xfmr_kva_rating
}) {
  // Constants
  const SQRT_3 = Math.sqrt(3);

  // 1. Calculate Full Load Current (FLC)
  // P = sqrt(3) * V_L * I_L * PF => I_L = P / (sqrt(3) * V_L * PF)
  const FLC = (motor_rated_power_kw * 1000) / (SQRT_3 * motor_rated_voltage_v * motor_power_factor_pu);

  // 2. Calculate Motor Start Inrush Current (LRC)
  const inrush_current_amps = FLC * motor_locked_rotor_current_multiple;

  // 3. Calculate Source Impedance (Ohms)
  // Z_source_ohms = V_L^2 / (S_sc_3ph) - where S_sc_3ph is in VA (MVA * 10^6)
  const Z_source_ohms = (motor_rated_voltage_v * motor_rated_voltage_v) / (source_short_circuit_mva * 1000000);

  // 4. Calculate Transformer Impedance (Ohms) if provided
  let Z_xfmr_ohms = 0;
  // Only consider transformer if both impedance and kVA rating are positive
  if (xfmr_impedance_percent > 0 && xfmr_kva_rating > 0) {
    // Z_xfmr_ohms = Z_pu * Z_base_xfmr, where Z_base_xfmr = V_rated_secondary^2 / S_rated_xfmr
    Z_xfmr_ohms = (xfmr_impedance_percent / 100) * ((motor_rated_voltage_v * motor_rated_voltage_v) / (xfmr_kva_rating * 1000));
  }

  // 5. Calculate Total System Impedance (Ohms) seen by the motor
  const system_impedance_ohms = Z_source_ohms + Z_xfmr_ohms;

  // 6. Calculate Voltage Dip (in pu, then percent)
  // Voltage Drop = I_inrush * Z_total
  // Voltage Dip (pu) = (I_inrush * Z_total) / V_rated
  let voltage_dip_percent = (inrush_current_amps * system_impedance_ohms / motor_rated_voltage_v) * 100;

  // Ensure voltage dip does not exceed 100%
  voltage_dip_percent = Math.min(voltage_dip_percent, 100);

  return {
    inrush_current_amps,
    voltage_dip_percent,
    system_impedance_ohms
  };
}
