const current_savings = inputs.current_savings || 0;
const annual_additional_savings = inputs.annual_additional_savings || 0;
const aer = (inputs.expected_annual_return || 0) / 100;
const aei = (inputs.expected_annual_inflation || 0) / 100;
const pdy = inputs.projection_duration_years || 0;

// Helper functions to handle zero rates and prevent division by zero
const futureValueLumpSum = (principal, rate, years) => {
    if (years <= 0) return principal;
    return principal * Math.pow(1 + rate, years);
};

const futureValueAnnuity = (payment, rate, years) => {
    if (years <= 0) return 0;
    if (rate === 0) {
        return payment * years;
    }
    return payment * (Math.pow(1 + rate, years) - 1) / rate;
};

// Calculate total portfolio value at the end of the projection period (nominal)
const projected_total_portfolio_nominal = futureValueLumpSum(current_savings, aer, pdy) +
                                          futureValueAnnuity(annual_additional_savings, aer, pdy);

// Calculate inflation-adjusted portfolio value at the end of the projection period (today's purchasing power)
const inflation_adjusted_portfolio = projected_total_portfolio_nominal / Math.pow(1 + aei, pdy);

// Goal 1 calculation
let goal1_attainment_status = "N/A (No target entered)";
const goal_1_target_amount = inputs.goal_1_target_amount || 0;
const goal_1_target_year = inputs.goal_1_target_year || 0;

if (goal_1_target_amount > 0 && goal_1_target_year > 0) {
    if (goal_1_target_year > pdy) {
        goal1_attainment_status = `Goal year (${goal_1_target_year} yrs) exceeds projection (${pdy} yrs).`;
    } else {
        const portfolio_at_g1y_nominal = futureValueLumpSum(current_savings, aer, goal_1_target_year) +
                                         futureValueAnnuity(annual_additional_savings, aer, goal_1_target_year);
        // Adjust goal amount for inflation to its target year (if target amount is in today's value)
        const goal1_needed_nominal_at_g1y = goal_1_target_amount * Math.pow(1 + aei, goal_1_target_year);
        const goal1_difference_nominal = portfolio_at_g1y_nominal - goal1_needed_nominal_at_g1y;

        if (goal1_difference_nominal >= 0) {
            goal1_attainment_status = `Met! Surplus by $${goal1_difference_nominal.toFixed(0)} (nominal).`;
        } else {
            goal1_attainment_status = `Short by $${Math.abs(goal1_difference_nominal).toFixed(0)} (nominal).`;
        }
    }
}

// Goal 2 calculation
let goal2_attainment_status = "N/A (No target entered)";
const goal_2_target_amount = inputs.goal_2_target_amount || 0;
const goal_2_target_year = inputs.goal_2_target_year || 0;

if (goal_2_target_amount > 0 && goal_2_target_year > 0) {
    if (goal_2_target_year > pdy) {
        goal2_attainment_status = `Goal year (${goal_2_target_year} yrs) exceeds projection (${pdy} yrs).`;
    } else {
        const portfolio_at_g2y_nominal = futureValueLumpSum(current_savings, aer, goal_2_target_year) +
                                         futureValueAnnuity(annual_additional_savings, aer, goal_2_target_year);
        // Adjust goal amount for inflation to its target year (if target amount is in today's value)
        const goal2_needed_nominal_at_g2y = goal_2_target_amount * Math.pow(1 + aei, goal_2_target_year);
        const goal2_difference_nominal = portfolio_at_g2y_nominal - goal2_needed_nominal_at_g2y;

        if (goal2_difference_nominal >= 0) {
            goal2_attainment_status = `Met! Surplus by $${goal2_difference_nominal.toFixed(0)} (nominal).`;
        } else {
            goal2_attainment_status = `Short by $${Math.abs(goal2_difference_nominal).toFixed(0)} (nominal).`;
        }
    }
}

// Overall sufficiency
let overall_sufficiency = "Your financial projection is complete.";
if (inflation_adjusted_portfolio < current_savings && inflation_adjusted_portfolio > 0) {
    overall_sufficiency = "Your inflation-adjusted portfolio is projected to decrease or barely grow. Consider increasing savings or returns.";
} else if (inflation_adjusted_portfolio <= 0) {
    overall_sufficiency = "Warning: Your inflation-adjusted portfolio is projected to be negative. Re-evaluate your strategy immediately.";
} else if ((goal1_attainment_status.includes("Short") && goal1_attainment_status.includes("Goal year") === false) || (goal2_attainment_status.includes("Short") && goal2_attainment_status.includes("Goal year") === false)) {
    overall_sufficiency = "Some goals are not met. Adjust your contributions or target amounts.";
} else if ((goal1_attainment_status.includes("Met")) || (goal2_attainment_status.includes("Met"))) {
    overall_sufficiency = "Great news! You are on track for at least one of your goals. Review details above.";
} else if (projected_total_portfolio_nominal > current_savings && inflation_adjusted_portfolio > current_savings) {
    overall_sufficiency = "Your financial position is projected to improve significantly. Keep up the good work!";
}


return {
    projected_total_portfolio: Math.round(projected_total_portfolio_nominal),
    inflation_adjusted_portfolio: Math.round(inflation_adjusted_portfolio),
    goal1_attainment_status: goal1_attainment_status,
    goal2_attainment_status: goal2_attainment_status,
    overall_sufficiency: overall_sufficiency
};
