const totalDistance = totalFlightDistanceKm; // km
let ecoShare = economyClassShare / 100; // as decimal
let busShare = businessClassShare / 100;
let firstShare = firstClassShare / 100;

// Basic validation and normalization for shares
let totalShareInput = ecoShare + busShare + firstShare;

if (totalShareInput === 0) {
    return { totalCO2: 0, co2PerPassengerKm: 0, treeOffset: 0 };
}

// If sum > 100% (e.g., due to user input error), normalize proportionally
if (totalShareInput > 1.001 || totalShareInput < 0.999) {
    const scaleFactor = 1 / totalShareInput;
    ecoShare *= scaleFactor;
    busShare *= scaleFactor;
    firstShare *= scaleFactor;
}

const baseEcoPkmFactor = 90; // Average g CO2 / pkm for economy class across various flights
const ecoMultiplier = 1.0;
const busMultiplier = 2.5; // Business class takes up more space, thus higher footprint per passenger
const firstMultiplier = 4.0; // First class takes up even more space per passenger

const radiativeForcingIndex = 1.9; // Accounts for non-CO2 effects at altitude (contrails, NOx, etc.), converting CO2 to CO2e

// Calculate effective CO2 per passenger-kilometer for the mixed trip based on class shares
const effectivePkmFactor = (
    (ecoShare * baseEcoPkmFactor * ecoMultiplier) +
    (busShare * baseEcoPkmFactor * busMultiplier) +
    (firstShare * baseEcoPkmFactor * firstMultiplier)
);

// Total CO2 emissions in grams, including RFI
const totalCO2grams = totalDistance * effectivePkmFactor * radiativeForcingIndex;

// Convert to kg CO2e
const totalCO2kg = totalCO2grams / 1000;

// Average CO2e per passenger-kilometer (g CO2e/pkm) including RFI
const co2PerPkmWithRFI = effectivePkmFactor * radiativeForcingIndex;

const treeAbsorptionKgPerYear = 22; // Average CO2 absorption by one mature tree per year (kg CO2)
const treesNeeded = totalCO2kg / treeAbsorptionKgPerYear;

return {
    totalCO2: totalCO2kg,
    co2PerPassengerKm: co2PerPkmWithRFI,
    treeOffset: treesNeeded
};
