const carDistanceKm = Number(inputs.carDistanceKm) || 0;
const carFuelEfficiencyL100km = Number(inputs.carFuelEfficiencyL100km) || 0;
const carPassengers = Number(inputs.carPassengers) || 0;
const planeDistanceKm = Number(inputs.planeDistanceKm) || 0;
const planeClass = (inputs.planeClass || 'economy').toLowerCase();
const trainDistanceKm = Number(inputs.trainDistanceKm) || 0;
const busDistanceKm = Number(inputs.busDistanceKm) || 0;

// Emission factors (kg CO2e per unit) - These are approximate global averages.
const EMISSION_FACTOR_PETROL_LITER = 2.3; // kg CO2e per liter of petrol
const EMISSION_FACTOR_PLANE_ECONOMY_PKM = 0.12; // kg CO2e per passenger-km (medium haul avg)
const EMISSION_FACTOR_PLANE_BUSINESS_MULTIPLIER = 2.5; // Business class is ~2.5x economy
const EMISSION_FACTOR_PLANE_FIRST_MULTIPLIER = 4.0; // First class is ~4.0x economy
const EMISSION_FACTOR_TRAIN_PKM = 0.03; // kg CO2e per passenger-km (average for electric/diesel mix)
const EMISSION_FACTOR_BUS_PKM = 0.04; // kg CO2e per passenger-km

let totalCarCo2 = 0;
let carCo2PerPerson = 0;
if (carDistanceKm > 0 && carFuelEfficiencyL100km > 0) {
    const fuelConsumedL = (carDistanceKm / 100) * carFuelEfficiencyL100km;
    totalCarCo2 = fuelConsumedL * EMISSION_FACTOR_PETROL_LITER;
    carCo2PerPerson = totalCarCo2 / Math.max(1, carPassengers + 1); // +1 for the driver
}

let planeCo2ForOnePerson = 0;
if (planeDistanceKm > 0) {
    let planeEmissionFactorPKM = EMISSION_FACTOR_PLANE_ECONOMY_PKM;
    if (planeClass === 'business') {
        planeEmissionFactorPKM *= EMISSION_FACTOR_PLANE_BUSINESS_MULTIPLIER;
    } else if (planeClass === 'first class') {
        planeEmissionFactorPKM *= EMISSION_FACTOR_PLANE_FIRST_MULTIPLIER;
    }
    planeCo2ForOnePerson = planeDistanceKm * planeEmissionFactorPKM;
}

let trainCo2ForOnePerson = 0;
if (trainDistanceKm > 0) {
    trainCo2ForOnePerson = trainDistanceKm * EMISSION_FACTOR_TRAIN_PKM;
}

let busCo2ForOnePerson = 0;
if (busDistanceKm > 0) {
    busCo2ForOnePerson = busDistanceKm * EMISSION_FACTOR_BUS_PKM;
}

// Total emissions for the entire journey as specified by segments and passenger counts for car.
const totalJourneyEmissions = totalCarCo2 + planeCo2ForOnePerson + trainCo2ForOnePerson + busCo2ForOnePerson;

// Personal share of emissions for a single traveler undertaking this journey.
const personalJourneyEmissions = carCo2PerPerson + planeCo2ForOnePerson + trainCo2ForOnePerson + busCo2ForOnePerson;

return {
    totalCarbonFootprintKgCO2: totalJourneyEmissions,
    carbonFootprintPerPersonKgCO2: personalJourneyEmissions
};
