function calculateNetEnergySecurityBenefit(inputs) {
  const { initialInfrastructureInvestment, annualOperatingMaintenanceCosts, domesticProductionIncrease, averageMarketPrice, geopoliticalRiskReductionFactor, averageAnnualInsecurityCost, economicMultiplier, analysisDurationYears, discountRate } = inputs;

  // Input validation and default handling
  const inv = Math.max(0, parseFloat(initialInfrastructureInvestment || 0));
  const opM = Math.max(0, parseFloat(annualOperatingMaintenanceCosts || 0));
  const prod = Math.max(0, parseFloat(domesticProductionIncrease || 0));
  const price = Math.max(0, parseFloat(averageMarketPrice || 0));
  const riskFactor = Math.min(1, Math.max(0, parseFloat(geopoliticalRiskReductionFactor || 0)));
  const avgInsecCost = Math.max(0, parseFloat(averageAnnualInsecurityCost || 0));
  const ecoMult = Math.max(1, parseFloat(economicMultiplier || 1));
  const duration = Math.max(1, parseInt(analysisDurationYears || 1));
  const discRate = Math.max(0, parseFloat(discountRate || 0.05));

  let totalProjectCostNPV = 0;
  let totalEconomicBenefitsNPV = 0;
  let geopoliticalRiskMitigationValueNPV = 0;

  // 1. Calculate Discounted Project Costs (NPV)
  totalProjectCostNPV += inv; // Initial investment is at time 0
  for (let t = 1; t <= duration; t++) {
    totalProjectCostNPV += opM / Math.pow(1 + discRate, t);
  }

  // 2. Calculate Discounted Economic Benefits from Increased Production (NPV)
  const annualProductionValue = prod * price * ecoMult;
  for (let t = 1; t <= duration; t++) {
    totalEconomicBenefitsNPV += annualProductionValue / Math.pow(1 + discRate, t);
  }

  // 3. Calculate Discounted Geopolitical Risk Mitigation Value (NPV)
  const annualRiskMitigationValue = avgInsecCost * riskFactor;
  for (let t = 1; t <= duration; t++) {
    geopoliticalRiskMitigationValueNPV += annualRiskMitigationValue / Math.pow(1 + discRate, t);
  }

  // 4. Calculate Net Energy Security Benefit (NPV)
  const netEnergySecurityBenefitNPV = (totalEconomicBenefitsNPV + geopoliticalRiskMitigationValueNPV) - totalProjectCostNPV;

  // 5. Calculate Return on Investment (ROI)
  let returnOnInvestment = 0;
  if (totalProjectCostNPV > 0) {
    returnOnInvestment = (netEnergySecurityBenefitNPV / totalProjectCostNPV) * 100;
  }

  // 6. Calculate Energy Independence Impact Score (Heuristic)
  // This score reflects the magnitude of domestic production increase relative to cost efficiency.
  // Max score for significant production increase and positive net benefit.
  let energyIndependenceImpactScore = 0;
  if (prod > 0 && inv > 0) {
    const productionPerDollar = prod * duration * price / totalProjectCostNPV;
    // Scale 'productionPerDollar' to a reasonable range for scoring. Max out at 100.
    // A higher economic multiplier further boosts the score.
    energyIndependenceImpactScore = Math.min(100, (productionPerDollar * ecoMult * 10) + (netEnergySecurityBenefitNPV > 0 ? 20 : 0));
  }
  // Ensure score is non-negative
  energyIndependenceImpactScore = Math.max(0, energyIndependenceImpactScore);

  return {
    totalProjectCostNPV,
    totalEconomicBenefitsNPV,
    geopoliticalRiskMitigationValueNPV,
    netEnergySecurityBenefitNPV,
    returnOnInvestment,
    energyIndependenceImpactScore: Math.round(energyIndependenceImpactScore)
  };
}
