function calculateEnergySecurity(inputs) {
    const weights = {
        energy_source_diversity_score: 0.15,
        net_import_dependency_ratio: 0.20,
        critical_infrastructure_resilience_score: 0.20,
        geopolitical_stability_of_suppliers_score: 0.15,
        strategic_petroleum_reserve_days: 0.10,
        renewable_energy_generation_share: 0.10,
        energy_efficiency_intensity_score: 0.05,
        cross_border_energy_interconnectivity_score: 0.05
    };

    // Normalize inputs to a 0-1 scale
    const normalizedInputs = {};
    let lowestNormalizedScore = Infinity;
    let lowestScoreId = '';

    // Energy Source Diversity Score (higher is better)
    normalizedInputs.energy_source_diversity_score = Math.max(0, Math.min(100, inputs.energy_source_diversity_score)) / 100;

    // Net Energy Import Dependency (lower is better, so invert for security score)
    normalizedInputs.net_import_dependency_ratio = (100 - Math.max(0, Math.min(100, inputs.net_import_dependency_ratio))) / 100;

    // Critical Infrastructure Resilience Score (higher is better)
    normalizedInputs.critical_infrastructure_resilience_score = Math.max(0, Math.min(100, inputs.critical_infrastructure_resilience_score)) / 100;

    // Geopolitical Stability of Suppliers Score (higher is better)
    normalizedInputs.geopolitical_stability_of_suppliers_score = Math.max(0, Math.min(100, inputs.geopolitical_stability_of_suppliers_score)) / 100;

    // Strategic Petroleum Reserve Days (cap at 180 days for full score, higher is better)
    normalizedInputs.strategic_petroleum_reserve_days = Math.min(Math.max(0, inputs.strategic_petroleum_reserve_days), 180) / 180;

    // Renewable Energy Share (higher is better)
    normalizedInputs.renewable_energy_generation_share = Math.max(0, Math.min(100, inputs.renewable_energy_generation_share)) / 100;

    // Energy Efficiency Intensity Score (higher is better)
    normalizedInputs.energy_efficiency_intensity_score = Math.max(0, Math.min(100, inputs.energy_efficiency_intensity_score)) / 100;

    // Cross-Border Energy Interconnectivity Score (higher is better)
    normalizedInputs.cross_border_energy_interconnectivity_score = Math.max(0, Math.min(100, inputs.cross_border_energy_interconnectivity_score)) / 100;

    // Identify the lowest normalized score (excluding specific inverted inputs for recommendation logic clarity)
    // For recommendation, we want the rawest sense of 'weakness'
    const rawNormalizedScoresForRecommendation = {
        'Energy Source Diversity': normalizedInputs.energy_source_diversity_score,
        'Import Dependency (Lower is Better)': normalizedInputs.net_import_dependency_ratio,
        'Infrastructure Resilience': normalizedInputs.critical_infrastructure_resilience_score,
        'Supplier Stability': normalizedInputs.geopolitical_stability_of_suppliers_score,
        'Strategic Reserves': normalizedInputs.strategic_petroleum_reserve_days,
        'Renewable Energy Share': normalizedInputs.renewable_energy_generation_share,
        'Energy Efficiency': normalizedInputs.energy_efficiency_intensity_score,
        'Cross-Border Interconnectivity': normalizedInputs.cross_border_energy_interconnectivity_score
    };

    for (const key in rawNormalizedScoresForRecommendation) {
        if (rawNormalizedScoresForRecommendation[key] < lowestNormalizedScore) {
            lowestNormalizedScore = rawNormalizedScoresForRecommendation[key];
            lowestScoreId = key;
        }
    }

    // Calculate the weighted sum
    let energy_security_index = 0;
    for (const key in weights) {
        energy_security_index += normalizedInputs[key] * weights[key];
    }

    // Convert index to a 0-100 scale
    energy_security_index = energy_security_index * 100;

    // Determine vulnerability rating
    let vulnerability_rating;
    if (energy_security_index < 40) {
        vulnerability_rating = "Critical";
    } else if (energy_security_index < 60) {
        vulnerability_rating = "High";
    } else if (energy_security_index < 75) {
        vulnerability_rating = "Moderate";
    } else {
        vulnerability_rating = "Low";
    }

    // Determine key recommendation area based on lowest normalized score
    let key_recommendation_area = `Focus on improving ${lowestScoreId}.`;
    if (lowestScoreId === 'Import Dependency (Lower is Better)') {
        key_recommendation_area = 'Prioritize reducing Net Energy Import Dependency.';
    } else if (lowestScoreId === 'Strategic Reserves') {
        key_recommendation_area = 'Enhance Strategic Petroleum Reserve Capacity.';
    } else if (lowestScoreId === 'Supplier Stability') {
        key_recommendation_area = 'Diversify energy suppliers and strengthen geopolitical ties.';
    } else if (lowestScoreId === 'Renewable Energy Share') {
        key_recommendation_area = 'Accelerate the transition to renewable energy sources.';
    } else if (lowestScoreId === 'Energy Efficiency') {
        key_recommendation_area = 'Implement policies to improve national energy efficiency.';
    } else if (lowestScoreId === 'Cross-Border Interconnectivity') {
        key_recommendation_area = 'Strengthen cross-border energy infrastructure and agreements.';
    } else if (lowestScoreId === 'Infrastructure Resilience') {
        key_recommendation_area = 'Invest in hardening critical energy infrastructure against threats.';
    } else if (lowestScoreId === 'Energy Source Diversity') {
        key_recommendation_area = 'Increase the diversity of national energy sources.';
    }

    return {
        energy_security_index: parseFloat(energy_security_index.toFixed(2)),
        vulnerability_rating: vulnerability_rating,
        key_recommendation_area: key_recommendation_area
    };
}
