const DENSITY_METHANE_KG_PER_M3 = 0.668; // kg/m³ at STP (0°C, 1 atm)
const DENSITY_ETHANE_KG_PER_M3 = 1.25;  // kg/m³ at STP
const DENSITY_PROPANE_KG_PER_M3 = 1.83; // kg/m³ at STP

// GWP values (AR6 approx, direct effects)
const GWP_METHANE_20YR = 84;
const GWP_METHANE_100YR = 28;
const GWP_ETHANE_20YR = 4; 
const GWP_ETHANE_100YR = 4;
const GWP_PROPANE_20YR = 3;
const GWP_PROPANE_100YR = 3;

const leakRate_m3_hr = parseFloat(inputs.leakRate_m3_hr);
const leakDuration_hr = parseFloat(inputs.leakDuration_hr);
const methane_percent = parseFloat(inputs.methane_percent);
const ethane_percent = parseFloat(inputs.ethane_percent);
const propane_percent = parseFloat(inputs.propane_percent);

if (isNaN(leakRate_m3_hr) || leakRate_m3_hr < 0 ||
    isNaN(leakDuration_hr) || leakDuration_hr < 0 ||
    isNaN(methane_percent) || methane_percent < 0 ||
    isNaN(ethane_percent) || ethane_percent < 0 ||
    isNaN(propane_percent) || propane_percent < 0) {
    return {
        totalMethaneLeaked_kg: 0,
        totalEthaneLeaked_kg: 0,
        totalPropaneLeaked_kg: 0,
        co2e_20yr_tonnes: 0,
        co2e_100yr_tonnes: 0
    };
}

let normalizedMethanePercent = methane_percent;
let normalizedEthanePercent = ethane_percent;
let normalizedPropanePercent = propane_percent;

const totalInputPercentage = methane_percent + ethane_percent + propane_percent;

// If percentages sum to more than 100, normalize them proportionally.
// This ensures the total gas volume isn't 'over-allocated' to components.
if (totalInputPercentage > 100.001) { 
    const scaleFactor = 100 / totalInputPercentage;
    normalizedMethanePercent *= scaleFactor;
    normalizedEthanePercent *= scaleFactor;
    normalizedPropanePercent *= scaleFactor;
}

const totalGasVolume_m3 = leakRate_m3_hr * leakDuration_hr;

const methaneVolume_m3 = totalGasVolume_m3 * (normalizedMethanePercent / 100);
const ethaneVolume_m3 = totalGasVolume_m3 * (normalizedEthanePercent / 100);
const propaneVolume_m3 = totalGasVolume_m3 * (normalizedPropanePercent / 100);

const totalMethaneLeaked_kg = methaneVolume_m3 * DENSITY_METHANE_KG_PER_M3;
const totalEthaneLeaked_kg = ethaneVolume_m3 * DENSITY_ETHANE_KG_PER_M3;
const totalPropaneLeaked_kg = propaneVolume_m3 * DENSITY_PROPANE_KG_PER_M3;

const methane_co2e_20yr_tonnes = (totalMethaneLeaked_kg * GWP_METHANE_20YR) / 1000;
const methane_co2e_100yr_tonnes = (totalMethaneLeaked_kg * GWP_METHANE_100YR) / 1000;

const ethane_co2e_20yr_tonnes = (totalEthaneLeaked_kg * GWP_ETHANE_20YR) / 1000;
const ethane_co2e_100yr_tonnes = (totalEthaneLeaked_kg * GWP_ETHANE_100YR) / 1000;

const propane_co2e_20yr_tonnes = (totalPropaneLeaked_kg * GWP_PROPANE_20YR) / 1000;
const propane_co2e_100yr_tonnes = (totalPropaneLeaked_kg * GWP_PROPANE_100YR) / 1000;

const co2e_20yr_tonnes = methane_co2e_20yr_tonnes + ethane_co2e_20yr_tonnes + propane_co2e_20yr_tonnes;
const co2e_100yr_tonnes = methane_co2e_100yr_tonnes + ethane_co2e_100yr_tonnes + propane_co2e_100yr_tonnes;

return {
    totalMethaneLeaked_kg: totalMethaneLeaked_kg,
    totalEthaneLeaked_kg: totalEthaneLeaked_kg,
    totalPropaneLeaked_kg: totalPropaneLeaked_kg,
    co2e_20yr_tonnes: co2e_20yr_tonnes,
    co2e_100yr_tonnes: co2e_100yr_tonnes
};
