const K_base = 0.00476; // Empirical constant for power estimation
const MIN_POWER_THRESHOLD = 500; // kW, minimum power for auxiliary systems

// Cap hull_coefficient to prevent unrealistic values or division by zero
const effectiveHullCoefficient = Math.max(0.4, Math.min(0.9, hull_coefficient));

// 1. Estimated Power Required (kW)
// Power is approximated using a modified Admiralty Coefficient principle:
// Power ~ Displacement^(2/3) * Speed^3 / HullEfficiency
let estimated_power = K_base * Math.pow(vessel_displacement, 2/3) * Math.pow(design_speed, 3) / effectiveHullCoefficient;

// Ensure a minimum power output for practical vessel operations
estimated_power = Math.max(estimated_power, MIN_POWER_THRESHOLD);

// 2. Fuel Consumption per Hour (kg/hr)
// engine_efficiency (SFC) is in g/kWh, so convert to kg/hr
let fuel_consumption_per_hour_kg = (estimated_power * engine_efficiency) / 1000;

// Handle cases where speed or power could theoretically be zero leading to zero consumption
if (design_speed <= 0 || estimated_power <= 0) {
    fuel_consumption_per_hour_kg = 0;
}

// 3. Fuel Consumption per Hour (liters/hr)
let fuel_consumption_per_hour_liters = fuel_consumption_per_hour_kg / fuel_density;

// 4. Total Fuel Capacity (liters and kg)
const total_fuel_capacity_liters = fuel_tank_capacity * 1000; // m^3 to liters
const total_fuel_capacity_kg = total_fuel_capacity_liters * fuel_density;

// 5. Operational Hours
let operational_hours = 0;
if (fuel_consumption_per_hour_kg > 0) {
    operational_hours = total_fuel_capacity_kg / fuel_consumption_per_hour_kg;
} else {
    operational_hours = Infinity; // If consumption is zero, theoretically infinite hours
}

// 6. Operational Range (nautical miles)
let operational_range_nm = operational_hours * design_speed;

// If design speed is 0, range is 0 regardless of fuel
if (design_speed === 0) {
    operational_range_nm = 0;
}

// 7. Total Fuel Cost (for full tank)
let total_fuel_cost = total_fuel_capacity_liters * fuel_price;

// 8. Cost per Nautical Mile
let cost_per_nm = 0;
if (operational_range_nm > 0) {
    cost_per_nm = total_fuel_cost / operational_range_nm;
} else if (operational_range_nm === 0 && total_fuel_cost > 0) {
    cost_per_nm = Infinity; // Costs money but cannot move
}

return {
    estimated_power: estimated_power,
    fuel_consumption_per_hour_kg: fuel_consumption_per_hour_kg,
    fuel_consumption_per_hour_liters: fuel_consumption_per_hour_liters,
    operational_range_nm: operational_range_nm,
    total_fuel_cost: total_fuel_cost,
    cost_per_nm: cost_per_nm
};
