const initialInvestment = inputs.initialInvestment;
const discountRate = inputs.discountRate / 100;

if (discountRate <= -1) {
    // Discount rate cannot be -100% or less, as (1+r) would be zero or negative
    return { netPresentValue: NaN, cumulativeDiscountedCashFlows: NaN };
}

let cumulativeDiscountedCashFlows = 0;

// Calculate discounted cash flows for each period
const cashFlow1 = inputs.cashFlowPeriod1 || 0;
cumulativeDiscountedCashFlows += cashFlow1 / Math.pow(1 + discountRate, 1);

const cashFlow2 = inputs.cashFlowPeriod2 || 0;
cumulativeDiscountedCashFlows += cashFlow2 / Math.pow(1 + discountRate, 2);

const cashFlow3 = inputs.cashFlowPeriod3 || 0;
cumulativeDiscountedCashFlows += cashFlow3 / Math.pow(1 + discountRate, 3);

const cashFlow4 = inputs.cashFlowPeriod4 || 0;
cumulativeDiscountedCashFlows += cashFlow4 / Math.pow(1 + discountRate, 4);

const cashFlow5 = inputs.cashFlowPeriod5 || 0;
cumulativeDiscountedCashFlows += cashFlow5 / Math.pow(1 + discountRate, 5);

const netPresentValue = initialInvestment + cumulativeDiscountedCashFlows;

return {
    netPresentValue: netPresentValue,
    cumulativeDiscountedCashFlows: cumulativeDiscountedCashFlows
};
