const currentNetWorth = inputs.currentNetWorth;
const annualSavings = inputs.annualSavings;
const nominalInvestmentRate = inputs.annualInvestmentReturnRate / 100;
const inflationRate = inputs.inflationRate / 100;
const numYears = inputs.numYears;

let projectedNetWorth = 0;
let netWorthInflationAdjusted = 0;

// Calculate Projected Net Worth (Nominal)
if (nominalInvestmentRate === 0) {
    projectedNetWorth = currentNetWorth + (annualSavings * numYears);
} else {
    const fvLumpSum = currentNetWorth * Math.pow(1 + nominalInvestmentRate, numYears);
    const fvAnnuity = annualSavings * ((Math.pow(1 + nominalInvestmentRate, numYears) - 1) / nominalInvestmentRate);
    projectedNetWorth = fvLumpSum + fvAnnuity;
}

// Calculate Net Worth in Today's Dollars (Inflation Adjusted)
// Real investment rate formula: ((1 + nominalRate) / (1 + inflationRate)) - 1
const realInvestmentRate = ((1 + nominalInvestmentRate) / (1 + inflationRate)) - 1;

if (realInvestmentRate === 0) {
    netWorthInflationAdjusted = currentNetWorth + (annualSavings * numYears);
} else {
    const fvLumpSum_real = currentNetWorth * Math.pow(1 + realInvestmentRate, numYears);
    const fvAnnuity_real = annualSavings * ((Math.pow(1 + realInvestmentRate, numYears) - 1) / realInvestmentRate);
    netWorthInflationAdjusted = fvLumpSum_real + fvAnnuity_real;
}

// Ensure net worth does not go below zero, useful for extreme negative rates/inflation
projectedNetWorth = Math.max(0, projectedNetWorth);
netWorthInflationAdjusted = Math.max(0, netWorthInflationAdjusted);

return {
    projectedNetWorth,
    netWorthInflationAdjusted
};
