const annualReturnDecimal = inputs.expectedAnnualReturn / 100;
const inflationDecimal = inputs.inflationRate / 100;
const savingsIncreaseDecimal = inputs.annualSavingsIncreaseRate ? inputs.annualSavingsIncreaseRate / 100 : 0;

let currentNetWorth = inputs.currentNetWorth;
let annualSavingsAmount = inputs.currentAnnualSavings;
let totalContributions = 0;
let totalInvestmentGrowth = 0;

for (let i = 0; i < inputs.yearsToProject; i++) {
    // Investment growth on current net worth
    const growthThisYear = currentNetWorth * annualReturnDecimal;
    currentNetWorth += growthThisYear;
    totalInvestmentGrowth += growthThisYear;

    // Add annual savings for this year
    currentNetWorth += annualSavingsAmount;
    totalContributions += annualSavingsAmount;

    // Increase annual savings for the next year, if applicable
    if (savingsIncreaseDecimal > 0) {
        annualSavingsAmount *= (1 + savingsIncreaseDecimal);
    }
}

const projectedNetWorth = currentNetWorth;
const realProjectedNetWorth = projectedNetWorth / Math.pow(1 + inflationDecimal, inputs.yearsToProject);

return {
    projectedNetWorth: projectedNetWorth,
    realProjectedNetWorth: realProjectedNetWorth,
    totalContributions: totalContributions,
    totalInvestmentGrowth: totalInvestmentGrowth
};
