function calculate({
  editorial_rigor,
  transparency_ownership,
  correction_policy,
  attribution_sourcing,
  journalistic_independence,
  bias_disclosure,
  separation_opinion_fact,
  sensationalism_frequency
}) {
  // Ensure all inputs are numbers and within valid range [0, 5]
  const inputs = {
    editorial_rigor: Math.max(0, Math.min(5, Number(editorial_rigor || 0))),
    transparency_ownership: Math.max(0, Math.min(5, Number(transparency_ownership || 0))),
    correction_policy: Math.max(0, Math.min(5, Number(correction_policy || 0))),
    attribution_sourcing: Math.max(0, Math.min(5, Number(attribution_sourcing || 0))),
    journalistic_independence: Math.max(0, Math.min(5, Number(journalistic_independence || 0))),
    bias_disclosure: Math.max(0, Math.min(5, Number(bias_disclosure || 0))),
    separation_opinion_fact: Math.max(0, Math.min(5, Number(separation_opinion_fact || 0))),
    sensationalism_frequency: Math.max(0, Math.min(5, Number(sensationalism_frequency || 0)))
  };

  // Calculate Overall Credibility Score
  const credibilityFactors = [
    inputs.editorial_rigor,
    inputs.transparency_ownership,
    inputs.correction_policy,
    inputs.attribution_sourcing,
    inputs.journalistic_independence,
    inputs.separation_opinion_fact
  ];
  const sumCredibilityFactors = credibilityFactors.reduce((sum, val) => sum + val, 0);
  const maxCredibilitySum = credibilityFactors.length * 5;
  let baseCredibility = (sumCredibilityFactors / maxCredibilitySum) * 100;

  // Apply penalty for sensationalism
  const sensationalismPenalty = (inputs.sensationalism_frequency / 5) * 25; // Max 25% penalty
  let finalCredibilityScore = Math.max(0, baseCredibility - sensationalismPenalty);
  finalCredibilityScore = Math.min(100, finalCredibilityScore); // Cap at 100%

  // Calculate Potential Bias Indicator
  // Higher values for these inputs (or inverse) indicate higher potential bias
  const biasInfluencingFactors = [
    (5 - inputs.journalistic_independence), // Lack of independence increases bias
    (5 - inputs.separation_opinion_fact),   // Poor separation increases bias
    (5 - inputs.bias_disclosure),         // Lack of bias disclosure increases bias
    inputs.sensationalism_frequency       // Sensationalism increases bias
  ];
  const sumBiasInfluencingFactors = biasInfluencingFactors.reduce((sum, val) => sum + val, 0);
  const maxBiasInfluencingSum = biasInfluencingFactors.length * 5; // 4 factors, max 5 each
  let potentialBiasIndicator = (sumBiasInfluencingFactors / maxBiasInfluencingSum) * 100;
  potentialBiasIndicator = Math.min(100, potentialBiasIndicator); // Cap at 100%

  // Determine Credibility Recommendation
  let credibilityRecommendation = "";
  if (finalCredibilityScore >= 85 && potentialBiasIndicator <= 20) {
    credibilityRecommendation = "Highly Credible & Minimal Bias: This source demonstrates strong adherence to journalistic ethics.";
  } else if (finalCredibilityScore >= 70 && potentialBiasIndicator <= 35) {
    credibilityRecommendation = "Generally Credible with Low Bias: A reliable source, but always consider diverse perspectives.";
  } else if (finalCredibilityScore >= 50 && potentialBiasIndicator <= 50) {
    credibilityRecommendation = "Moderate Credibility, Potential for Bias: Exercise caution and cross-reference information.";
  } else {
    credibilityRecommendation = "Low Credibility, Significant Bias Concerns: This source may not adhere to fundamental journalistic standards. Verify information independently.";
  }

  return {
    credibility_score: finalCredibilityScore,
    potential_bias_indicator: potentialBiasIndicator,
    credibility_recommendation: credibilityRecommendation
  };
}
