function calculateNPV(rate, initialInvestment, cashFlows) {
    let npv = -initialInvestment;
    for (let t = 0; t < cashFlows.length; t++) {
        npv += cashFlows[t] / Math.pow(1 + rate, t + 1);
    }
    return npv;
}

const initialInvestment = parseFloat(inputs.initialInvestment);
const discountRate = parseFloat(inputs.discountRate) / 100;
const cashFlows = inputs.cashFlows.split(',').map(Number);

// Input validation
if (isNaN(initialInvestment) || initialInvestment < 0) {
    return { npv: 'Invalid Initial Investment', irr: 'N/A' };
}
if (isNaN(discountRate) || discountRate < 0) {
    return { npv: 'Invalid Discount Rate', irr: 'N/A' };
}
if (cashFlows.some(isNaN) || cashFlows.length === 0) {
    return { npv: 'Invalid Cash Flows', irr: 'N/A' };
}

// Calculate NPV
const npvResult = calculateNPV(discountRate, initialInvestment, cashFlows);

// IRR Calculation using bisection method
let irrResult = 'N/A';
const irrTolerance = 0.000001; // For 0.0001% precision
const maxIrrIterations = 1000;

// Check if a meaningful IRR can exist (i.e., there's a mix of positive and negative cash flows)
const totalCashFlowAfterInitial = cashFlows.reduce((sum, cf) => sum + cf, 0);
const hasPositiveCF = cashFlows.some(cf => cf > 0) || (initialInvestment < 0 && totalCashFlowAfterInitial >= 0);
const hasNegativeCF = initialInvestment > 0 || cashFlows.some(cf => cf < 0) || (initialInvestment < 0 && totalCashFlowAfterInitial < 0);

if (hasPositiveCF && hasNegativeCF) {
    let lowRate = -0.9999; // Allows for IRR down to -99.99%
    let highRate = 500;   // Allows for IRR up to 50,000%
    let iterations = 0;

    while (iterations < maxIrrIterations) {
        const midRate = (lowRate + highRate) / 2;
        if (midRate <= -1) { // Avoid 1+rate being zero or negative, shifting range
            lowRate = midRate;
            iterations++;
            continue;
        }
        const currentNPVforIRR = calculateNPV(midRate, initialInvestment, cashFlows);

        if (Math.abs(currentNPVforIRR) < irrTolerance) {
            irrResult = midRate * 100;
            break;
        }

        if (currentNPVforIRR > 0) { // NPV is positive, rate is too low, need higher rate
            lowRate = midRate;
        } else { // NPV is negative, rate is too high, need lower rate
            highRate = midRate;
        }
        iterations++;
    }

    // If not converged sufficiently after max iterations, check proximity
    if (irrResult === 'N/A' && Math.abs(highRate - lowRate) < irrTolerance * 100) {
        irrResult = ((lowRate + highRate) / 2) * 100;
    }

    // Cap IRR at reasonable display limits
    if (typeof irrResult === 'number') {
        if (irrResult < -99.99) irrResult = -99.99;
        if (irrResult > 50000) irrResult = 50000; // Cap at 50,000%
    }
} else if (!hasPositiveCF && initialInvestment >= 0) {
    irrResult = 'No IRR (all outflows)';
} else if (!hasNegativeCF && initialInvestment < 0) {
    irrResult = 'No IRR (all inflows)'; // Not typically analyzed with IRR/NPV this way
}

return {
    npv: npvResult,
    irr: irrResult
};
